import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export interface Record {
  id: number;
  ministry: string;
  vendor: string;
  amount: number;
  method: string;
  date: string;
  reason?: string | null;
}

export type ViewState = 'dashboard' | 'upload' | 'ministry';

export interface ViewConfig {
  view: ViewState;
  ministryName?: string;
}

export const formatMoney = (amount: number): string => {
  return new Intl.NumberFormat('en-MY', {
    style: 'currency',
    currency: 'MYR',
    maximumFractionDigits: 0,
  }).format(amount).replace('MYR', 'RM');
};

export const formatDate = (dateStr: string): string => {
  return dateStr; // Already YYYY-MM-DD, keeping as is for table compactness or use format if needed
};

import React from 'react';
import { Eye, Upload, LayoutDashboard } from 'lucide-react';

interface LayoutProps {
  children: React.ReactNode;
  activeView: string;
  onNavigate: (view: 'dashboard' | 'upload') => void;
}

export const Layout: React.FC<LayoutProps> = ({ children, activeView, onNavigate }) => {
  return (
    <div className="min-h-screen bg-gw-bg text-gw-text font-sans">
      <nav className="sticky top-0 z-50 bg-gw-bg/95 backdrop-blur border-b border-gw-border">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center gap-3 cursor-pointer" onClick={() => onNavigate('dashboard')}>
              <div className="bg-gw-success/10 p-2 rounded-lg">
                <Eye className="w-6 h-6 text-gw-success" />
              </div>
              <span className="font-bold text-xl tracking-tight">GovWatch <span className="text-gw-muted font-normal">MY</span></span>
            </div>
            <div className="flex space-x-4">
              <button
                onClick={() => onNavigate('dashboard')}
                className={`flex items-center px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                  activeView === 'dashboard' 
                    ? 'bg-gw-card text-white' 
                    : 'text-gw-muted hover:text-white hover:bg-gw-card/50'
                }`}
              >
                <LayoutDashboard className="w-4 h-4 mr-2" />
                Dashboard
              </button>
              <button
                onClick={() => onNavigate('upload')}
                className={`flex items-center px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                  activeView === 'upload' 
                    ? 'bg-gw-card text-white' 
                    : 'text-gw-muted hover:text-white hover:bg-gw-card/50'
                }`}
              >
                <Upload className="w-4 h-4 mr-2" />
                Upload Data
              </button>
            </div>
          </div>
        </div>
      </nav>
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {children}
      </main>
    </div>
  );
};

import React from 'react';

interface StatCardProps {
  label: string;
  value: string | number | React.ReactNode;
  isAlert?: boolean;
}

export const StatCard: React.FC<StatCardProps> = ({ label, value, isAlert }) => {
  return (
    <div className="bg-gw-card border border-gw-border rounded-lg p-6 shadow-sm">
      <div className="text-sm font-medium text-gw-muted uppercase tracking-wider mb-2">
        {label}
      </div>
      <div className={`text-3xl font-bold truncate ${isAlert ? 'text-gw-danger' : 'text-gw-success'}`}>
        {value}
      </div>
    </div>
  );
};

import React from 'react';
import { Record } from '../types';
import { StatCard } from '../components/StatCard';
import { formatMoney } from '../utils';
import { ArrowUpRight, Search, FileText } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Cell, TooltipProps } from 'recharts';

interface DashboardProps {
  records: Record[];
  onMinistryClick: (name: string) => void;
}

const CustomTooltip = ({ active, payload, label }: TooltipProps<number, string>) => {
  if (active && payload && payload.length) {
    return (
      <div className="bg-gw-card border border-gw-border p-3 rounded shadow-xl">
        <p className="text-gw-text font-bold text-sm">{label}</p>
        <p className="text-gw-success text-sm">{formatMoney(payload[0].value as number)}</p>
      </div>
    );
  }
  return null;
};

export const Dashboard: React.FC<DashboardProps> = ({ records, onMinistryClick }) => {
  const totalSpend = records.reduce((acc, r) => acc + r.amount, 0);
  const directSpend = records
    .filter(r => r.method.toLowerCase().includes('direct'))
    .reduce((acc, r) => acc + r.amount, 0);
  const share = totalSpend ? (directSpend / totalSpend * 100) : 0;

  // Aggregate data for chart
  const ministryTotals = records.reduce((acc, curr) => {
    // Shorten ministry names for chart readability
    const shortName = curr.ministry.replace('Kementerian', 'Kem.').replace('Jabatan', 'Jab.');
    acc[shortName] = (acc[shortName] || 0) + curr.amount;
    return acc;
  }, {} as Record<string, number>);

  const chartData = Object.entries(ministryTotals)
    .map(([name, value]) => ({ name, value }))
    .sort((a, b) => b.value - a.value)
    .slice(0, 5); // Top 5

  return (
    <div className="space-y-8 animate-fadeIn">
      {/* KPI Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <StatCard label="Total Tracked" value={formatMoney(totalSpend)} />
        <StatCard label="Direct Negotiation" value={formatMoney(directSpend)} isAlert />
        <StatCard 
          label="Risk Share" 
          value={
            <div className="flex items-center gap-2">
              {share.toFixed(1)}%
              <span className="text-xs font-normal text-gw-muted bg-gw-bg px-2 py-1 rounded-full border border-gw-border">
                of total spend
              </span>
            </div>
          } 
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Recent Records Table */}
        <div className="lg:col-span-2 bg-gw-card border border-gw-border rounded-lg overflow-hidden">
          <div className="p-6 border-b border-gw-border flex justify-between items-center">
            <h2 className="text-xl font-bold text-white">Latest Records</h2>
            <div className="text-sm text-gw-muted flex items-center gap-2">
              <FileText className="w-4 h-4" />
              {records.length} records found
            </div>
          </div>
          <div className="overflow-x-auto">
            <table className="w-full text-left text-sm">
              <thead className="bg-gw-bg/50">
                <tr>
                  <th className="px-6 py-4 font-medium text-gw-muted uppercase text-xs tracking-wider">Date</th>
                  <th className="px-6 py-4 font-medium text-gw-muted uppercase text-xs tracking-wider">Ministry</th>
                  <th className="px-6 py-4 font-medium text-gw-muted uppercase text-xs tracking-wider">Vendor</th>
                  <th className="px-6 py-4 font-medium text-gw-muted uppercase text-xs tracking-wider text-right">Value</th>
                  <th className="px-6 py-4 font-medium text-gw-muted uppercase text-xs tracking-wider text-right">Method</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gw-border">
                {records.sort((a,b) => new Date(b.date).getTime() - new Date(a.date).getTime()).map((r) => (
                  <tr key={r.id} className="hover:bg-gw-bg/30 transition-colors group">
                    <td className="px-6 py-4 whitespace-nowrap text-gw-muted">{r.date}</td>
                    <td className="px-6 py-4">
                      <button 
                        onClick={() => onMinistryClick(r.ministry)}
                        className="text-gw-text hover:text-gw-success font-medium flex items-center gap-1 transition-colors text-left"
                      >
                        {r.ministry}
                        <ArrowUpRight className="w-3 h-3 opacity-0 group-hover:opacity-100 transition-opacity" />
                      </button>
                    </td>
                    <td className="px-6 py-4 text-gw-muted truncate max-w-[150px]">{r.vendor}</td>
                    <td className="px-6 py-4 text-right font-medium text-gw-text">{formatMoney(r.amount)}</td>
                    <td className="px-6 py-4 text-right whitespace-nowrap">
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                        r.method.toLowerCase().includes('direct')
                          ? 'bg-gw-danger/10 text-gw-danger border border-gw-danger/20'
                          : 'bg-gw-success/10 text-gw-success border border-gw-success/20'
                      }`}>
                        {r.method}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Mini Chart */}
        <div className="bg-gw-card border border-gw-border rounded-lg p-6 flex flex-col">
          <h3 className="text-lg font-bold mb-6 text-white">Top Spenders</h3>
          <div className="flex-1 min-h-[300px]">
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={chartData} layout="vertical" margin={{ left: 0, right: 30 }}>
                <XAxis type="number" hide />
                <YAxis 
                  dataKey="name" 
                  type="category" 
                  width={100} 
                  tick={{ fill: '#8da2ce', fontSize: 11 }}
                  interval={0}
                />
                <Tooltip cursor={{fill: '#223055'}} content={<CustomTooltip />} />
                <Bar dataKey="value" radius={[0, 4, 4, 0]}>
                  {chartData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={index === 0 ? '#36d399' : '#223055'} />
                  ))}
                </Bar>
              </BarChart>
            </ResponsiveContainer>
          </div>
          <p className="text-xs text-gw-muted mt-4 text-center italic">
            Top 5 ministries by total contract value
          </p>
        </div>
      </div>
    </div>
  );
};

import React from 'react';
import { Record } from '../types';
import { formatMoney } from '../utils';
import { ArrowLeft, Building2, Calendar, CreditCard, FileWarning } from 'lucide-react';

interface MinistryDetailProps {
  ministryName: string;
  records: Record[];
  onBack: () => void;
}

export const MinistryDetail: React.FC<MinistryDetailProps> = ({ ministryName, records, onBack }) => {
  const filteredRecords = records.filter(r => r.ministry === ministryName).sort((a,b) => new Date(b.date).getTime() - new Date(a.date).getTime());
  const totalSpend = filteredRecords.reduce((acc, r) => acc + r.amount, 0);

  return (
    <div className="space-y-6 animate-fadeIn">
      <button 
        onClick={onBack}
        className="flex items-center text-gw-muted hover:text-gw-text transition-colors mb-4 group"
      >
        <ArrowLeft className="w-4 h-4 mr-2 group-hover:-translate-x-1 transition-transform" />
        Back to Dashboard
      </button>

      <div className="bg-gw-card border border-gw-border rounded-lg p-8 shadow-lg">
        <div className="flex flex-col md:flex-row md:items-end justify-between gap-4 border-b border-gw-border pb-6 mb-6">
            <div>
                <div className="flex items-center gap-3 mb-2">
                    <div className="p-2 bg-gw-bg rounded border border-gw-border">
                        <Building2 className="w-6 h-6 text-gw-muted" />
                    </div>
                    <span className="text-gw-muted uppercase tracking-wider text-sm font-semibold">Ministry Report</span>
                </div>
                <h1 className="text-3xl font-bold text-white">{ministryName}</h1>
            </div>
            <div className="text-left md:text-right">
                <div className="text-sm text-gw-muted mb-1">Total Contract Value</div>
                <div className="text-3xl font-bold text-gw-success">{formatMoney(totalSpend)}</div>
            </div>
        </div>

        <div className="grid grid-cols-1 gap-4">
            {filteredRecords.map((r) => (
                <div key={r.id} className="bg-gw-bg border border-gw-border rounded-lg p-5 hover:border-gw-muted/50 transition-colors">
                    <div className="flex flex-col md:flex-row justify-between md:items-start gap-4 mb-4">
                        <div className="flex-1">
                            <h3 className="text-lg font-bold text-white mb-1">{r.vendor}</h3>
                            <div className="flex items-center gap-4 text-sm text-gw-muted">
                                <span className="flex items-center gap-1">
                                    <Calendar className="w-3 h-3" /> {r.date}
                                </span>
                                <span className="flex items-center gap-1">
                                    <CreditCard className="w-3 h-3" /> {r.method}
                                </span>
                            </div>
                        </div>
                        <div className={`text-xl font-bold ${r.method.includes('Direct') ? 'text-gw-danger' : 'text-gw-success'}`}>
                            {formatMoney(r.amount)}
                        </div>
                    </div>

                    {r.reason ? (
                        <div className="bg-gw-card/50 rounded p-3 text-sm text-gw-text border border-gw-border/50 flex gap-2 items-start">
                             <FileWarning className="w-4 h-4 text-gw-danger shrink-0 mt-0.5" />
                             <div>
                                <span className="text-gw-danger font-semibold uppercase text-xs tracking-wide block mb-1">Direct Negotiation Justification</span>
                                {r.reason}
                             </div>
                        </div>
                    ) : (
                        <div className="text-xs text-gw-muted italic opacity-50">Standard procurement via open tender process.</div>
                    )}
                </div>
            ))}
        </div>
      </div>
    </div>
  );
};

import React, { useState } from 'react';
import { Upload as UploadIcon, FileSpreadsheet, CheckCircle, AlertCircle } from 'lucide-react';

export const Upload: React.FC = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [status, setStatus] = useState<'idle' | 'uploading' | 'success' | 'error'>('idle');

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    simulateUpload();
  };

  const simulateUpload = () => {
    setStatus('uploading');
    setTimeout(() => {
      setStatus('success');
    }, 1500);
  };

  return (
    <div className="max-w-2xl mx-auto animate-fadeIn">
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold text-white mb-2">Upload Procurement Data</h1>
        <p className="text-gw-muted">Import Excel (.xlsx) files to update the dashboard.</p>
      </div>

      <div 
        className={`bg-gw-card border-2 border-dashed rounded-xl p-12 text-center transition-all duration-200 ${
          isDragging 
            ? 'border-gw-success bg-gw-success/5' 
            : 'border-gw-border'
        }`}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        {status === 'idle' && (
          <>
            <div className="bg-gw-bg w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4 border border-gw-border">
              <UploadIcon className="w-8 h-8 text-gw-muted" />
            </div>
            <h3 className="text-lg font-medium text-white mb-2">Drag and drop your Excel file here</h3>
            <p className="text-gw-muted text-sm mb-6">Or click below to browse your files</p>

            <input 
              type="file" 
              id="file-upload" 
              className="hidden" 
              accept=".xlsx"
              onChange={simulateUpload}
            />
            <label 
              htmlFor="file-upload"
              className="inline-flex items-center px-6 py-3 border border-transparent text-base font-medium rounded-md shadow-sm text-gw-bg bg-gw-success hover:bg-gw-success/90 cursor-pointer transition-colors"
            >
              Select File
            </label>
            <div className="mt-4 flex items-center justify-center gap-2 text-xs text-gw-muted opacity-60">
                <FileSpreadsheet className="w-3 h-3" />
                <span>Supports .xlsx format only</span>
            </div>
          </>
        )}

        {status === 'uploading' && (
          <div className="py-8">
            <div className="w-12 h-12 border-4 border-gw-success border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
            <p className="text-gw-text font-medium">Processing Data...</p>
            <p className="text-sm text-gw-muted mt-2">Parsing Direct Negotiation cards...</p>
          </div>
        )}

        {status === 'success' && (
          <div className="py-8 animate-fadeIn">
            <div className="w-16 h-16 bg-gw-success/10 rounded-full flex items-center justify-center mx-auto mb-4">
                <CheckCircle className="w-10 h-10 text-gw-success" />
            </div>
            <h3 className="text-xl font-bold text-white mb-2">Upload Successful</h3>
            <p className="text-gw-muted mb-6">Database has been updated with new records.</p>
            <button 
                onClick={() => setStatus('idle')}
                className="text-gw-success hover:text-gw-success/80 underline text-sm"
            >
                Upload another file
            </button>
          </div>
        )}
      </div>

      <div className="mt-8 bg-gw-card/50 border border-gw-border rounded-lg p-6">
        <h4 className="font-bold text-white mb-2 flex items-center gap-2">
            <AlertCircle className="w-4 h-4 text-gw-muted" />
            Note for Scrapers
        </h4>
        <p className="text-sm text-gw-muted leading-relaxed">
            Ensure your Excel file follows the standardized schema: 
            <code className="bg-gw-bg px-1 py-0.5 rounded text-xs mx-1 border border-gw-border">Ministry</code>, 
            <code className="bg-gw-bg px-1 py-0.5 rounded text-xs mx-1 border border-gw-border">Vendor</code>, 
            <code className="bg-gw-bg px-1 py-0.5 rounded text-xs mx-1 border border-gw-border">Amount</code>, 
            <code className="bg-gw-bg px-1 py-0.5 rounded text-xs mx-1 border border-gw-border">Method</code>.
            Rows missing required fields will be skipped automatically.
        </p>
      </div>
    </div>
  );
};

import React, { useState } from 'react';
import { Layout } from './components/Layout';
import { Dashboard } from './views/Dashboard';
import { MinistryDetail } from './views/MinistryDetail';
import { Upload } from './views/Upload';
import { INITIAL_RECORDS } from './data';
import { ViewConfig } from './types';

function App() {
  const [viewConfig, setViewConfig] = useState<ViewConfig>({ view: 'dashboard' });
  const [records] = useState(INITIAL_RECORDS);

  const handleNavigate = (view: 'dashboard' | 'upload') => {
    setViewConfig({ view });
    window.scrollTo(0, 0);
  };

  const handleMinistryClick = (ministryName: string) => {
    setViewConfig({ view: 'ministry', ministryName });
    window.scrollTo(0, 0);
  };

  return (
    <Layout activeView={viewConfig.view} onNavigate={handleNavigate}>
      {viewConfig.view === 'dashboard' && (
        <Dashboard 
          records={records} 
          onMinistryClick={handleMinistryClick} 
        />
      )}

      {viewConfig.view === 'ministry' && viewConfig.ministryName && (
        <MinistryDetail 
          ministryName={viewConfig.ministryName} 
          records={records}
          onBack={() => handleNavigate('dashboard')}
        />
      )}

      {viewConfig.view === 'upload' && (
        <Upload />
      )}
    </Layout>
  );
}

export default App;

