  const [records, setRecords] = useState<Record[]>(INITIAL_RECORDS);

  const handleNavigate = (view: 'dashboard' | 'upload') => {
    setViewConfig({ view });
    window.scrollTo(0, 0);
  };

  const handleMinistryClick = (ministryName: string) => {
    setViewConfig({ view: 'ministry', ministryName });
    window.scrollTo(0, 0);
  };

  const handleDataLoaded = (newRecords: Record[]) => {
    setRecords(newRecords);
    handleNavigate('dashboard');
  };

  return (
    <Layout activeView={viewConfig.view} onNavigate={handleNavigate}>
      {viewConfig.view === 'dashboard' && (
        <Dashboard 
          records={records} 
          onMinistryClick={handleMinistryClick} 
          onUploadRequest={() => handleNavigate('upload')}
        />
      )}

      {viewConfig.view === 'ministry' && viewConfig.ministryName && (
        <MinistryDetail 
          ministryName={viewConfig.ministryName} 
          records={records}
          onBack={() => handleNavigate('dashboard')}
        />
      )}

      {viewConfig.view === 'upload' && (
        <Upload onDataLoaded={handleDataLoaded} />
      )}
    </Layout>
  );
}

export default App;

import React, { useState } from 'react';
import { Record } from '../types';
import { StatCard } from '../components/StatCard';
import { formatMoney } from '../utils';
import { ArrowUpRight, Search, FileText, Upload, AlertCircle } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Cell } from 'recharts';

interface DashboardProps {
  records: Record[];
  onMinistryClick: (name: string) => void;
  onUploadRequest: () => void;
}

const CustomTooltip = ({ active, payload, label }: any) => {
  if (active && payload && payload.length) {
    return (
      <div className="bg-gw-card border border-gw-border p-3 rounded shadow-xl">
        <p className="text-gw-text font-bold text-sm">{label}</p>
        <p className="text-gw-success text-sm">{formatMoney(payload[0].value as number)}</p>
      </div>
    );
  }
  return null;
};

export const Dashboard: React.FC<DashboardProps> = ({ records, onMinistryClick, onUploadRequest }) => {
  const [searchTerm, setSearchTerm] = useState('');

  // EMPTY STATE HANDLER
  if (records.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center min-h-[60vh] text-center animate-fadeIn p-4">
        <div className="bg-gw-card border border-gw-border rounded-full p-6 mb-6 shadow-[0_0_30px_rgba(54,211,153,0.1)]">
          <Upload className="w-12 h-12 text-gw-success" />
        </div>
        <h2 className="text-2xl font-bold text-white mb-2">No Records Found</h2>
        <p className="text-gw-muted max-w-md mb-8">
          The database is currently empty. Please upload the latest procurement data JSON file to generate the dashboard.
        </p>
        <button
          onClick={onUploadRequest}
          className="bg-gw-success hover:bg-gw-success/90 text-gw-bg font-bold py-3 px-8 rounded-lg transition-all flex items-center gap-2"
        >
          <Upload className="w-5 h-5" />
          Upload Data
        </button>
      </div>
    );
  }

  const filteredRecords = records.filter(r => 
    r.ministry.toLowerCase().includes(searchTerm.toLowerCase()) ||
    r.vendor.toLowerCase().includes(searchTerm.toLowerCase())
  ).sort((a,b) => new Date(b.date).getTime() - new Date(a.date).getTime());

  const totalSpend = records.reduce((acc, r) => acc + r.amount, 0);
  const directSpend = records
    .filter(r => r.method.toLowerCase().includes('direct'))
    .reduce((acc, r) => acc + r.amount, 0);
  const share = totalSpend ? (directSpend / totalSpend * 100) : 0;

  // Aggregate data for chart
  const ministryTotals = records.reduce((acc, curr) => {
    // Shorten ministry names for chart readability
    let shortName = curr.ministry.split('(')[1]?.replace(')', '') || curr.ministry.substring(0, 10);
    acc[shortName] = (acc[shortName] || 0) + curr.amount;
    return acc;
  }, {} as { [key: string]: number });

  const chartData = Object.entries(ministryTotals)
    .map(([name, value]) => ({ name, value }))
    .sort((a, b) => b.value - a.value)
    .slice(0, 10); // Top 10

  return (
    <div className="space-y-8 animate-fadeIn">
