                  {chartData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={index < 3 ? '#ffadad' : '#36d399'} />
                  ))}
                </Bar>
              </BarChart>
            </ResponsiveContainer>
          </div>
          <p className="text-xs text-gw-muted mt-4 text-center italic">
            Ministries by total contract value (Red indicates top 3 spenders)
