};

import React, { useState } from 'react';
import { Upload as UploadIcon, FileJson, CheckCircle, AlertCircle, AlertTriangle } from 'lucide-react';
import { Record } from '../types';

interface UploadProps {
  onDataLoaded: (data: Record[]) => void;
}

export const Upload: React.FC<UploadProps> = ({ onDataLoaded }) => {
  const [isDragging, setIsDragging] = useState(false);
  const [status, setStatus] = useState<'idle' | 'processing' | 'error'>('idle');
  const [errorMessage, setErrorMessage] = useState('');

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    const file = e.dataTransfer.files[0];
    if (file) processFile(file);
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) processFile(file);
  };

  const processFile = (file: File) => {
    if (!file.name.endsWith('.json')) {
      setStatus('error');
      setErrorMessage('Please upload a .json file generated by the scraper script.');
      return;
    }

    setStatus('processing');
    const reader = new FileReader();

    reader.onload = (e) => {
      try {
        const text = e.target?.result as string;
        const data = JSON.parse(text);

        if (!Array.isArray(data)) {
          throw new Error("File content is not an array");
        }

        // Basic validation
        const isValid = data.every(item => 
          item.ministry && 
          item.vendor && 
          typeof item.amount === 'number'
        );

        if (!isValid) {
          throw new Error("Invalid data structure. Missing required fields.");
        }

        // Success - add slight delay for UX
        setTimeout(() => {
            onDataLoaded(data as Record[]);
        }, 800);

      } catch (err) {
        setStatus('error');
        setErrorMessage('Failed to parse JSON file. Ensure it is formatted correctly.');
        console.error(err);
      }
    };

    reader.onerror = () => {
      setStatus('error');
      setErrorMessage('Error reading file.');
    };

    reader.readAsText(file);
  };

  return (
    <div className="max-w-2xl mx-auto animate-fadeIn">
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold text-white mb-2">Import Data</h1>
        <p className="text-gw-muted">Upload the <code>govwatch_data.json</code> file generated from the scraper.</p>
      </div>

      <div 
        className={`bg-gw-card border-2 border-dashed rounded-xl p-12 text-center transition-all duration-200 ${
          isDragging 
            ? 'border-gw-success bg-gw-success/5' 
            : status === 'error' ? 'border-gw-danger'
            : 'border-gw-border'
        }`}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        {status === 'idle' && (
          <>
            <div className="bg-gw-bg w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4 border border-gw-border">
              <UploadIcon className="w-8 h-8 text-gw-muted" />
            </div>
            <h3 className="text-lg font-medium text-white mb-2">Drag and drop your JSON file</h3>
            <p className="text-gw-muted text-sm mb-6">Or click below to browse</p>

            <input 
              type="file" 
              id="file-upload" 
              className="hidden" 
              accept=".json"
              onChange={handleFileSelect}
            />
            <label 
              htmlFor="file-upload"
              className="inline-flex items-center px-6 py-3 border border-transparent text-base font-medium rounded-md shadow-sm text-gw-bg bg-gw-success hover:bg-gw-success/90 cursor-pointer transition-colors"
            >
              Select File
            </label>
            <div className="mt-4 flex items-center justify-center gap-2 text-xs text-gw-muted opacity-60">
                <FileJson className="w-3 h-3" />
                <span>Supports .json format only</span>
            </div>
          </>
        )}

        {status === 'processing' && (
          <div className="py-8">
            <div className="w-12 h-12 border-4 border-gw-success border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
            <p className="text-gw-text font-medium">Processing Data...</p>
            <p className="text-sm text-gw-muted mt-2">Validating records structure...</p>
          </div>
        )}

        {status === 'error' && (
          <div className="py-8 animate-fadeIn">
            <div className="w-16 h-16 bg-gw-danger/10 rounded-full flex items-center justify-center mx-auto mb-4">
                <AlertTriangle className="w-10 h-10 text-gw-danger" />
            </div>
            <h3 className="text-xl font-bold text-white mb-2">Upload Failed</h3>
            <p className="text-gw-danger mb-6">{errorMessage}</p>
            <button 
                onClick={() => setStatus('idle')}
                className="text-gw-text hover:text-white underline text-sm"
            >
                Try again
            </button>
          </div>
        )}
      </div>

      <div className="mt-8 bg-gw-card/50 border border-gw-border rounded-lg p-6">
        <h4 className="font-bold text-white mb-2 flex items-center gap-2">
            <AlertCircle className="w-4 h-4 text-gw-muted" />
            How to get data?
        </h4>
        <ol className="text-sm text-gw-muted leading-relaxed list-decimal list-inside space-y-2 mt-3">
            <li>Go to <a href="https://myprocurement.treasury.gov.my/results/tender" target="_blank" className="text-gw-success hover:underline">myprocurement.treasury.gov.my</a></li>
            <li>Open Developer Tools (F12) and go to the <strong>Console</strong> tab.</li>
            <li>Copy and paste the code from <code>scraper.js</code> provided in this project.</li>
            <li>The script will auto-download a JSON file.</li>
            <li>Upload that file here.</li>
        </ol>
      </div>
    </div>
  );
};

{
  "name": "govwatch-scraper",
  "version": "1.0.0",
  "description": "Automated scraper for GovWatch MY",
  "main": "scheduler.js",
  "type": "module",
  "scripts": {
    "start": "node scheduler.js",
    "scrape": "node scraper.js"
  },
  "dependencies": {
    "puppeteer": "^22.0.0",
    "node-cron": "^3.0.3"
  }
}

import puppeteer from 'puppeteer';
import fs from 'fs/promises';
import path from 'path';
import { fileURLToPath } from 'url';

const __dirname = path.dirname(fileURLToPath(import.meta.url));

// Configuration
const TARGET_URL = 'https://myprocurement.treasury.gov.my/results/tender';
const OUTPUT_PATH = path.resolve(__dirname, '../public/data.json'); // Saves to frontend public folder

async function scrape() {
  console.log(`[${new Date().toISOString()}] Starting scraping job...`);

  const browser = await puppeteer.launch({
    headless: "new",
    args: ['--no-sandbox', '--disable-setuid-sandbox']
  });

  const page = await browser.newPage();

  try {
    // 1. Navigate to site
    await page.goto(TARGET_URL, { waitUntil: 'networkidle2', timeout: 60000 });
    console.log('Page loaded. Waiting for table...');

    // 2. Wait for data to load
    await page.waitForSelector('table tbody tr', { timeout: 30000 });

    let allRecords = [];
    let hasNextPage = true;
    let pageNum = 1;

    // 3. Pagination Loop
    while (hasNextPage) {
        console.log(`Scraping page ${pageNum}...`);

        // Extract data from current page
        const pageData = await page.evaluate(() => {
            const rows = Array.from(document.querySelectorAll('table tbody tr'));
            return rows.map((row) => {
                const cols = row.querySelectorAll('td');
                if (cols.length < 5) return null;

                const parseAmount = (str) => {
                    if (!str) return 0;
                    return parseFloat(str.replace(/[^0-9.-]+/g, ""));
                };

                return {
                    // Generate ID based on content to keep it consistent
                    date: cols[0]?.innerText?.trim() || new Date().toISOString().split('T')[0],
                    ministry: cols[1]?.innerText?.trim() || "Unknown",
                    vendor: cols[2]?.innerText?.trim() || "Unknown",
                    amount: parseAmount(cols[3]?.innerText),
                    method: cols[4]?.innerText?.trim() || "Unknown",
                    reason: null 
                };
            }).filter(r => r !== null);
        });

        allRecords = [...allRecords, ...pageData];

        // Check for "Next" button
        // Note: Selector depends on specific pagination UI of the site. 
        // This is a generic robust selector for "Next" or ">" buttons often found in datatables.
        const nextButton = await page.$('button[aria-label="Next"], .pagination-next, li.next a');

        if (nextButton) {
            const isDisabled = await page.evaluate(el => el.hasAttribute('disabled') || el.classList.contains('disabled'), nextButton);
            if (!isDisabled) {
                await nextButton.click();
                await new Promise(r => setTimeout(r, 2000)); // Wait for table refresh
                pageNum++;
            } else {
                hasNextPage = false;
            }
        } else {
            hasNextPage = false;
        }

        // Safety break for testing (remove in production to scrape ALL pages)
        if (pageNum > 5) {
            console.log('Safety limit reached (5 pages). Stopping.');
            break; 
        }
    }

    // 4. Post-process: Add IDs
    const finalData = allRecords.map((r, i) => ({ ...r, id: i + 1 }));

    // 5. Save to file
    // Ensure public dir exists
    const publicDir = path.dirname(OUTPUT_PATH);
    try { await fs.access(publicDir); } catch { await fs.mkdir(publicDir, { recursive: true }); }

    await fs.writeFile(OUTPUT_PATH, JSON.stringify(finalData, null, 2));

    console.log(`[Success] Scraped ${finalData.length} records. Saved to ${OUTPUT_PATH}`);

  } catch (error) {
    console.error('[Error] Scraping failed:', error);
  } finally {
    await browser.close();
  }
}

// Execute if run directly
if (process.argv[1] === fileURLToPath(import.meta.url)) {
  scrape();
}

export { scrape };

import cron from 'node-cron';
import { scrape } from './scraper.js';

console.log('GovWatch Scheduler Started.');
console.log('Schedule: Every day at 03:00 AM');

// Run immediately on startup
scrape();

// Schedule task: Run at 03:00 AM every day
cron.schedule('0 3 * * *', () => {
  console.log('Running scheduled scrape job...');
  scrape();
});

import React, { useState, useEffect } from 'react';
import { Layout } from './components/Layout';
import { Dashboard } from './views/Dashboard';
import { MinistryDetail } from './views/MinistryDetail';
import { Upload } from './views/Upload';
import { INITIAL_RECORDS } from './data';
import { ViewConfig, Record } from './types';

function App() {
  const [viewConfig, setViewConfig] = useState<ViewConfig>({ view: 'dashboard' });
  const [records, setRecords] = useState<Record[]>(INITIAL_RECORDS);
  const [isLoading, setIsLoading] = useState(true);

  // Auto-fetch data on startup
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Attempt to fetch the file generated by the backend scraper
        const response = await fetch('/data.json');
        if (!response.ok) {
          throw new Error('Data file not found');
        }
        const data = await response.json();

        if (Array.isArray(data) && data.length > 0) {
          setRecords(data);
        }
      } catch (error) {
        console.log('Auto-fetch failed or no data present. Falling back to manual mode.');
        // We stay with INITIAL_RECORDS (empty)
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleNavigate = (view: 'dashboard' | 'upload') => {
    setViewConfig({ view });
    window.scrollTo(0, 0);
  };

  const handleMinistryClick = (ministryName: string) => {
    setViewConfig({ view: 'ministry', ministryName });
    window.scrollTo(0, 0);
  };

  const handleDataLoaded = (newRecords: Record[]) => {
    setRecords(newRecords);
    handleNavigate('dashboard');
  };

  return (
    <Layout activeView={viewConfig.view} onNavigate={handleNavigate}>
      {viewConfig.view === 'dashboard' && (
        <Dashboard 
          records={records} 
          isLoading={isLoading}
          onMinistryClick={handleMinistryClick} 
          onUploadRequest={() => handleNavigate('upload')}
        />
      )}

      {viewConfig.view === 'ministry' && viewConfig.ministryName && (
        <MinistryDetail 
          ministryName={viewConfig.ministryName} 
          records={records}
          onBack={() => handleNavigate('dashboard')}
        />
      )}

      {viewConfig.view === 'upload' && (
        <Upload onDataLoaded={handleDataLoaded} />
      )}
    </Layout>
  );
}

export default App;

import React, { useState } from 'react';
import { Record } from '../types';
import { StatCard } from '../components/StatCard';
import { formatMoney } from '../utils';
import { ArrowUpRight, Search, FileText, Upload, AlertCircle, RefreshCw } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Cell } from 'recharts';

interface DashboardProps {
  records: Record[];
  isLoading?: boolean;
  onMinistryClick: (name: string) => void;
  onUploadRequest: () => void;
}

const CustomTooltip = ({ active, payload, label }: any) => {
  if (active && payload && payload.length) {
    return (
      <div className="bg-gw-card border border-gw-border p-3 rounded shadow-xl">
        <p className="text-gw-text font-bold text-sm">{label}</p>
        <p className="text-gw-success text-sm">{formatMoney(payload[0].value as number)}</p>
      </div>
    );
  }
  return null;
};

export const Dashboard: React.FC<DashboardProps> = ({ records, isLoading, onMinistryClick, onUploadRequest }) => {
  const [searchTerm, setSearchTerm] = useState('');

  // LOADING STATE
  if (isLoading) {
    return (
        <div className="flex flex-col items-center justify-center min-h-[60vh] text-center animate-fadeIn">
            <RefreshCw className="w-10 h-10 text-gw-success animate-spin mb-4" />
            <h2 className="text-xl font-bold text-white">Connecting to Database...</h2>
            <p className="text-gw-muted text-sm">Checking for latest scraped records</p>
        </div>
    );
  }

  // EMPTY STATE HANDLER
  if (records.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center min-h-[60vh] text-center animate-fadeIn p-4">
