import puppeteer from 'puppeteer';
import fs from 'fs/promises';
import path from 'path';
import { fileURLToPath } from 'url';

const __dirname = path.dirname(fileURLToPath(import.meta.url));

// Configuration
const TARGET_URL = 'https://myprocurement.treasury.gov.my/results/tender';
const OUTPUT_PATH = path.resolve(__dirname, '../public/data.json');

async function scrape() {
  console.log(`[${new Date().toISOString()}] Starting scraping job...`);

  // Launch config for Server Environment
  const browser = await puppeteer.launch({
    headless: "new",
    executablePath: process.env.PUPPETEER_EXECUTABLE_PATH || undefined,
    args: [
        '--no-sandbox', 
        '--disable-setuid-sandbox',
        '--disable-dev-shm-usage', // critical for low-memory server environments
        '--single-process' // critical for some server environments
    ]
  });

  const page = await browser.newPage();

  // Set a real User Agent so the government site doesn't block the "robot"
  await page.setUserAgent('Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36');

  try {
    console.log('Navigating to site...');
    await page.goto(TARGET_URL, { waitUntil: 'networkidle2', timeout: 60000 });

    console.log('Waiting for table data...');
    // Increase timeout for slow government servers
    await page.waitForSelector('table tbody tr', { timeout: 45000 });

    let allRecords = [];
    let hasNextPage = true;
    let pageNum = 1;

    // LIMITER: Only scrape first 5 pages to prevent server timeout during initial test
    // You can remove (pageNum > 5) to scrape everything later.
    while (hasNextPage && pageNum <= 5) {
        console.log(`Scraping page ${pageNum}...`);

        const pageData = await page.evaluate(() => {
            const rows = Array.from(document.querySelectorAll('table tbody tr'));
            return rows.map((row) => {
                const cols = row.querySelectorAll('td');
                if (cols.length < 5) return null;

                const parseAmount = (str) => {
                    if (!str) return 0;
                    return parseFloat(str.replace(/[^0-9.-]+/g, ""));
                };

                return {
                    date: cols[0]?.innerText?.trim() || new Date().toISOString().split('T')[0],
                    ministry: cols[1]?.innerText?.trim() || "Unknown",
                    vendor: cols[2]?.innerText?.trim() || "Unknown",
                    amount: parseAmount(cols[3]?.innerText),
                    method: cols[4]?.innerText?.trim() || "Unknown",
                };
            }).filter(r => r !== null);
        });

        allRecords = [...allRecords, ...pageData];

        // Pagination Logic
        const nextButton = await page.$('li.next:not(.disabled) a, button[aria-label="Next"]:not([disabled])');

        if (nextButton) {
            await nextButton.click();
            // Wait for the table to update (loading state usually appears)
            await new Promise(r => setTimeout(r, 3000));
            pageNum++;
        } else {
            hasNextPage = false;
        }
    }

    // Post-process
    const finalData = allRecords.map((r, i) => ({ ...r, id: i + 1 }));

    // Ensure public directory exists
    const publicDir = path.dirname(OUTPUT_PATH);
    try { await fs.access(publicDir); } catch { await fs.mkdir(publicDir, { recursive: true }); }

    await fs.writeFile(OUTPUT_PATH, JSON.stringify(finalData, null, 2));
    console.log(`[Success] Scraped ${finalData.length} records. Saved to data.json`);

  } catch (error) {
    console.error('[Error] Scraping failed:', error);
  } finally {
    await browser.close();
  }
}

if (process.argv[1] === fileURLToPath(import.meta.url)) {
  scrape();
}

export { scrape };

import React, { useState, useEffect } from 'react';
import { Layout } from './components/Layout';
import { Dashboard } from './views/Dashboard';
import { MinistryDetail } from './views/MinistryDetail';
import { Upload } from './views/Upload';
import { INITIAL_RECORDS } from './data';
import { ViewConfig, Record } from './types';

function App() {
  const [viewConfig, setViewConfig] = useState<ViewConfig>({ view: 'dashboard' });
  const [records, setRecords] = useState<Record[]>(INITIAL_RECORDS);
  const [isLoading, setIsLoading] = useState(true);

  console.log("GovWatch App Rendering...");

  // Auto-fetch data on startup
  useEffect(() => {
    const fetchData = async () => {
      console.log("Attempting to fetch /data.json...");
      try {
        // Attempt to fetch the file generated by the backend scraper
        const response = await fetch('/data.json');

        if (!response.ok) {
          console.warn(`Fetch failed with status: ${response.status}`);
          throw new Error('Data file not found');
        }

        const data = await response.json();
        console.log(`Loaded ${data.length} records.`);

        if (Array.isArray(data) && data.length > 0) {
          setRecords(data);
        }
      } catch (error) {
        console.error('Auto-fetch failed. Using empty state.', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleNavigate = (view: 'dashboard' | 'upload') => {
    setViewConfig({ view });
    window.scrollTo(0, 0);
  };

  const handleMinistryClick = (ministryName: string) => {
    setViewConfig({ view: 'ministry', ministryName });
    window.scrollTo(0, 0);
  };

  const handleDataLoaded = (newRecords: Record[]) => {
    setRecords(newRecords);
    handleNavigate('dashboard');
  };

  return (
    <Layout activeView={viewConfig.view} onNavigate={handleNavigate}>
      {viewConfig.view === 'dashboard' && (
        <Dashboard 
          records={records} 
          isLoading={isLoading}
          onMinistryClick={handleMinistryClick} 
          onUploadRequest={() => handleNavigate('upload')}
        />
      )}

      {viewConfig.view === 'ministry' && viewConfig.ministryName && (
        <MinistryDetail 
          ministryName={viewConfig.ministryName} 
          records={records}
          onBack={() => handleNavigate('dashboard')}
        />
      )}

      {viewConfig.view === 'upload' && (
        <Upload onDataLoaded={handleDataLoaded} />
      )}
    </Layout>
  );
}

export default App;

