import React, { useState } from 'react';
import { Upload as UploadIcon, FileJson, AlertTriangle, Lock, CheckCircle } from 'lucide-react';
import { Record } from '../types';

interface UploadProps {
  onDataLoaded: (data: Record[]) => void;
}

export const Upload: React.FC<UploadProps> = ({ onDataLoaded }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [pin, setPin] = useState('');

  const [isDragging, setIsDragging] = useState(false);
  const [status, setStatus] = useState<'idle' | 'processing' | 'error' | 'success'>('idle');
  const [errorMessage, setErrorMessage] = useState('');

  // HARDCODED PIN FOR MANUAL ADMIN ACCESS
  const ADMIN_PIN = "admin2024";

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (pin === ADMIN_PIN) {
        setIsAuthenticated(true);
    } else {
        alert("Incorrect Access Code");
        setPin('');
    }
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    const file = e.dataTransfer.files[0];
    if (file) processFile(file);
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) processFile(file);
  };

  const processFile = (file: File) => {
    if (!file.name.endsWith('.json')) {
      setStatus('error');
      setErrorMessage('Please upload the govwatch_data.json file (JSON format).');
      return;
    }

    setStatus('processing');
    const reader = new FileReader();

    reader.onload = async (e) => {
      try {
        const text = e.target?.result as string;
        const data = JSON.parse(text);

        if (!Array.isArray(data)) {
          throw new Error("File content is not an array");
        }

        // Send data to backend for permanent storage
        const response = await fetch('/api/update-data', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(data)
        });

        if (!response.ok) {
            throw new Error('Server failed to save data');
        }

        setStatus('success');

        // Success - add slight delay for UX then redirect
        setTimeout(() => {
            onDataLoaded(data as Record[]);
        }, 1500);

      } catch (err) {
        setStatus('error');
        setErrorMessage('Failed to process or save file.');
        console.error(err);
      }
    };

    reader.onerror = () => {
      setStatus('error');
      setErrorMessage('Error reading file.');
    };

    reader.readAsText(file);
  };

  // 1. LOGIN SCREEN
  if (!isAuthenticated) {
    return (
        <div className="flex flex-col items-center justify-center min-h-[50vh] animate-fadeIn">
            <div className="bg-gw-card p-8 rounded-lg border border-gw-border shadow-2xl max-w-sm w-full">
                <div className="flex justify-center mb-6">
                    <div className="p-4 bg-gw-bg rounded-full border border-gw-border">
                        <Lock className="w-8 h-8 text-gw-muted" />
                    </div>
                </div>
                <h2 className="text-xl font-bold text-white text-center mb-6">Restricted Access</h2>
                <form onSubmit={handleLogin}>
                    <input 
                        type="password" 
                        value={pin}
                        onChange={(e) => setPin(e.target.value)}
                        placeholder="Enter Admin PIN"
                        className="w-full bg-gw-bg border border-gw-border text-center text-white p-3 rounded mb-4 focus:border-gw-success focus:outline-none tracking-widest"
                        autoFocus
                    />
                    <button 
                        type="submit"
                        className="w-full bg-gw-success text-gw-bg font-bold py-3 rounded hover:bg-gw-success/90 transition-colors"
                    >
                        Unlock
                    </button>
                </form>
                <p className="text-xs text-center text-gw-muted mt-4">Authorized personnel only.</p>
            </div>
        </div>
    );
  }

  // 2. UPLOAD SCREEN (Authenticated)
  return (
    <div className="max-w-2xl mx-auto animate-fadeIn">
      <div className="text-center mb-8">
        <div className="inline-block px-3 py-1 rounded-full bg-gw-success/10 text-gw-success text-xs font-bold mb-4 border border-gw-success/20">
            ADMIN MODE ACTIVE
        </div>
        <h1 className="text-3xl font-bold text-white mb-2">Update Database</h1>
        <p className="text-gw-muted">Upload the processed <code>govwatch_data.json</code> file.</p>
      </div>

      <div 
        className={`bg-gw-card border-2 border-dashed rounded-xl p-12 text-center transition-all duration-200 ${
          isDragging 
            ? 'border-gw-success bg-gw-success/5' 
            : status === 'error' ? 'border-gw-danger'
            : status === 'success' ? 'border-gw-success'
            : 'border-gw-border'
        }`}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        {status === 'idle' && (
          <>
            <div className="bg-gw-bg w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4 border border-gw-border">
              <UploadIcon className="w-8 h-8 text-gw-muted" />
            </div>
            <h3 className="text-lg font-medium text-white mb-2">Drag and drop JSON file</h3>
            <p className="text-gw-muted text-sm mb-6">Or click below to browse</p>

            <input 
              type="file" 
              id="file-upload" 
              className="hidden" 
              accept=".json"
              onChange={handleFileSelect}
            />
            <label 
              htmlFor="file-upload"
              className="inline-flex items-center px-6 py-3 border border-transparent text-base font-medium rounded-md shadow-sm text-gw-bg bg-gw-success hover:bg-gw-success/90 cursor-pointer transition-colors"
            >
              Select File
            </label>
          </>
        )}

        {status === 'processing' && (
          <div className="py-8">
            <div className="w-12 h-12 border-4 border-gw-success border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
            <p className="text-gw-text font-medium">Uploading to Server...</p>
          </div>
        )}

        {status === 'success' && (
          <div className="py-8">
            <div className="w-16 h-16 bg-gw-success/10 rounded-full flex items-center justify-center mx-auto mb-4">
                <CheckCircle className="w-10 h-10 text-gw-success" />
            </div>
            <h3 className="text-xl font-bold text-white mb-2">Update Successful</h3>
            <p className="text-gw-muted">Redirecting to dashboard...</p>
          </div>
        )}

        {status === 'error' && (
          <div className="py-8 animate-fadeIn">
            <div className="w-16 h-16 bg-gw-danger/10 rounded-full flex items-center justify-center mx-auto mb-4">
                <AlertTriangle className="w-10 h-10 text-gw-danger" />
            </div>
            <h3 className="text-xl font-bold text-white mb-2">Upload Failed</h3>
            <p className="text-gw-danger mb-6">{errorMessage}</p>
            <button 
                onClick={() => setStatus('idle')}
                className="text-gw-text hover:text-white underline text-sm"
            >
                Try again
            </button>
          </div>
        )}
      </div>

      <div className="mt-8 bg-gw-card/50 border border-gw-border rounded-lg p-6">
        <h4 className="font-bold text-white mb-2 flex items-center gap-2">
            <Lock className="w-4 h-4 text-gw-muted" />
            Secure Upload
        </h4>
        <p className="text-sm text-gw-muted">
            This interface is only accessible via the secret admin URL. Files uploaded here are immediately saved to the server and will be visible to public users.
        </p>
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Layout } from './components/Layout';
import { Dashboard } from './views/Dashboard';
import { MinistryDetail } from './views/MinistryDetail';
import { Upload } from './views/Upload';
import { INITIAL_RECORDS } from './data';
import { ViewConfig, Record } from './types';

function App() {
  const [viewConfig, setViewConfig] = useState<ViewConfig>({ view: 'dashboard' });
  const [records, setRecords] = useState<Record[]>(INITIAL_RECORDS);
  const [isLoading, setIsLoading] = useState(true);

  // Auto-fetch data on startup
  const fetchData = async () => {
    try {
      const response = await fetch('/data.json');
      if (response.ok) {
        const data = await response.json();
        if (Array.isArray(data) && data.length > 0) {
          setRecords(data);
        }
      } else {
        // File doesn't exist yet (first run), just use empty state
        console.log('No existing data found, starting with empty database.');
      }
    } catch (error) {
      console.warn('Network error checking for data:', error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchData();

    // SECRET ROUTE LISTENER
    // Checks the URL hash to see if the user is trying to access the admin panel
    const checkHash = () => {
      if (window.location.hash === '#secret-admin-panel') {
        setViewConfig({ view: 'upload' });
      }
    };

    // Check on load
    checkHash();

    // Check on hash change (if user types it in after loading)
    window.addEventListener('hashchange', checkHash);
    return () => window.removeEventListener('hashchange', checkHash);
  }, []);

  const handleNavigate = (view: 'dashboard' | 'upload') => {
    setViewConfig({ view });
    window.scrollTo(0, 0);
    // Clear hash if leaving upload
    if (view === 'dashboard' && window.location.hash === '#secret-admin-panel') {
        history.pushState("", document.title, window.location.pathname + window.location.search);
    }
  };

  const handleMinistryClick = (ministryName: string) => {
    setViewConfig({ view: 'ministry', ministryName });
    window.scrollTo(0, 0);
  };

  const handleDataLoaded = (newRecords: Record[]) => {
    // When upload finishes, update state and go to dashboard
    setRecords(newRecords);
    handleNavigate('dashboard');
  };

  return (
    <Layout activeView={viewConfig.view} onNavigate={handleNavigate}>
      {viewConfig.view === 'dashboard' && (
        <Dashboard 
          records={records} 
          isLoading={isLoading}
          onMinistryClick={handleMinistryClick} 
        />
      )}

      {viewConfig.view === 'ministry' && viewConfig.ministryName && (
        <MinistryDetail 
          ministryName={viewConfig.ministryName} 
          records={records}
          onBack={() => handleNavigate('dashboard')}
        />
      )}

      {viewConfig.view === 'upload' && (
        <Upload onDataLoaded={handleDataLoaded} />
      )}
    </Layout>
  );
}

export default App;

