import React, { useState, useEffect } from 'react';
import { Layout } from './components/Layout';
import { Dashboard } from './views/Dashboard';
import { MinistryDetail } from './views/MinistryDetail';
import { MinistryList } from './views/MinistryList';
import { VendorList } from './views/VendorList';
import { Upload } from './views/Upload';
import { INITIAL_RECORDS } from './data';
import { ViewConfig, Record } from './types';

function App() {
  const [viewConfig, setViewConfig] = useState<ViewConfig>({ view: 'dashboard' });
  const [records, setRecords] = useState<Record[]>(INITIAL_RECORDS);
  const [isLoading, setIsLoading] = useState(true);

  // Auto-fetch data on startup
  const fetchData = async () => {
    try {
      const response = await fetch('/data.json');
      if (response.ok) {
        const rawData = await response.json();

        // NORMALIZE DATA
        const cleanData: Record[] = [];
        if (Array.isArray(rawData)) {
             rawData.forEach((item: any, index: number) => {
                // 1. Skip Header Rows
                if (item['__EMPTY'] === 'TAJUK SEBUT HARGA' || item['__EMPTY_3'] === 'KEMENTERIAN') return;

                // 2. Map fields
                const ministry = item.ministry || item['__EMPTY_3'] || "Unknown Ministry";
                const vendor = item.vendor || item['__EMPTY_4'] || "Unknown Vendor";

                // Parse Amount
                let amount = 0;
                if (typeof item.amount === 'number') amount = item.amount;
                else if (item['__EMPTY_8']) {
                    amount = parseFloat(String(item['__EMPTY_8']).replace(/[^0-9.-]+/g, ""));
                } else if (typeof item.amount === 'string') {
                    amount = parseFloat(item.amount.replace(/[^0-9.-]+/g, ""));
                }

                // Parse Date
                let date = item.date || item['__EMPTY_6'];
                if (!date || date === 'TIADA MAKLUMAT') date = item['__EMPTY_7'];
                if (!date || date === 'TIADA MAKLUMAT') date = new Date().toISOString().split('T')[0];
                if (date && typeof date === 'string' && date.includes(' ')) {
                    date = date.split(' ')[0];
                }

                // Map Category & Method
                // Important: __EMPTY_2 is often "KATEGORI PEROLEHAN" (Bekalan, Kerja, Perkhidmatan)
                // We map this to 'category'.
                const category = item.category || item['__EMPTY_2'] || "General";

                // Raw data doesn't explicitly have "Direct Negotiation" column usually, so we default method.
                const method = item.method || "Open Tender";

                if (ministry !== "Unknown Ministry" || amount > 0) {
                    cleanData.push({
                        id: index + 1,
                        ministry: String(ministry).trim(),
                        vendor: String(vendor).trim(),
                        amount: amount || 0,
                        method: String(method).trim(),
                        category: String(category).trim(),
                        date: String(date).trim(),
                        reason: item.reason || null
                    });
                }
             });
        }

        if (cleanData.length > 0) {
          setRecords(cleanData);
        }
      }
    } catch (error) {
      console.warn('Network error checking for data:', error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchData();

    const checkHash = () => {
      if (window.location.hash === '#secret-admin-panel') {
        setViewConfig({ view: 'upload' });
      }
    };

    checkHash();
    window.addEventListener('hashchange', checkHash);
    return () => window.removeEventListener('hashchange', checkHash);
  }, []);

  const handleNavigate = (view: any) => {
    setViewConfig({ view });
    window.scrollTo(0, 0);
    if (view === 'dashboard' && window.location.hash === '#secret-admin-panel') {
        history.pushState("", document.title, window.location.pathname + window.location.search);
    }
  };

  const handleMinistryClick = (ministryName: string) => {
    setViewConfig({ view: 'ministry_detail', ministryName });
    window.scrollTo(0, 0);
  };

  const handleDataLoaded = (newRecords: Record[]) => {
    setRecords(newRecords);
    handleNavigate('dashboard');
  };

  return (
    <Layout activeView={viewConfig.view} onNavigate={handleNavigate}>
      {viewConfig.view === 'dashboard' && (
        <Dashboard 
          records={records} 
          isLoading={isLoading}
          onMinistryClick={handleMinistryClick} 
        />
      )}

      {viewConfig.view === 'ministry_detail' && viewConfig.ministryName && (
        <MinistryDetail 
          ministryName={viewConfig.ministryName} 
          records={records}
          onBack={() => handleNavigate('ministry_list')}
        />
      )}

      {viewConfig.view === 'ministry_list' && (
        <MinistryList 
            records={records} 
            onSelectMinistry={handleMinistryClick} 
        />
      )}

      {viewConfig.view === 'vendor_list' && (
        <VendorList records={records} />
      )}

      {viewConfig.view === 'upload' && (
        <Upload onDataLoaded={handleDataLoaded} />
      )}
    </Layout>
  );
}

export default App;

import React, { useState } from 'react';
import { Upload as UploadIcon, FileJson, AlertTriangle, Lock, CheckCircle, Copy, Terminal, Server, Play, Loader2 } from 'lucide-react';
import { Record } from '../types';

interface UploadProps {
  onDataLoaded: (data: Record[]) => void;
}

export const Upload: React.FC<UploadProps> = ({ onDataLoaded }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [pin, setPin] = useState('');

  const [isDragging, setIsDragging] = useState(false);
  const [status, setStatus] = useState<'idle' | 'processing' | 'error' | 'success'>('idle');
  const [scrapeStatus, setScrapeStatus] = useState<'idle' | 'running' | 'success' | 'error'>('idle');
  const [errorMessage, setErrorMessage] = useState('');
  const [copied, setCopied] = useState(false);

  // HARDCODED PIN FOR MANUAL ADMIN ACCESS
  const ADMIN_PIN = "admin2024";

  const SCRAPER_SCRIPT = `(function() {
  const rows = document.querySelectorAll('table tbody tr');
  const data = [];

  const parseAmount = (str) => {
    if (!str) return 0;
    return parseFloat(str.replace(/[^0-9.-]+/g, ""));
  };

  console.log('Processing ' + rows.length + ' rows...');

  rows.forEach((row, index) => {
    const cols = row.querySelectorAll('td');
    if (cols.length < 4) return;

    // Attempt to safely extract text
    const getText = (i) => cols[i]?.innerText?.trim() || "";

    const record = {
      id: index + 1,
      date: getText(0) || new Date().toISOString().split('T')[0],
      ministry: getText(1) || "Unknown Ministry",
      vendor: getText(2) || "Unknown Vendor",
      amount: parseAmount(getText(3)), 
      category: getText(4) || "General", // Use Column 4 as Category
      method: "Open Tender",
      reason: null
    };

    data.push(record);
  });

  const jsonString = JSON.stringify(data, null, 2);
  const blob = new Blob([jsonString], { type: "application/json" });
  const url = URL.createObjectURL(blob);

  const a = document.createElement('a');
  a.href = url;
  a.download = 'govwatch_data.json';
  document.body.appendChild(a);
  a.click();
  document.body.removeChild(a);
  URL.revokeObjectURL(url);

  alert('Successfully extracted ' + data.length + ' records! Upload the govwatch_data.json file to the dashboard.');
})();`;

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (pin === ADMIN_PIN) {
        setIsAuthenticated(true);
    } else {
        alert("Incorrect Access Code");
        setPin('');
    }
  };

  const handleCopyScript = () => {
    navigator.clipboard.writeText(SCRAPER_SCRIPT);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  // --- MANUAL UPLOAD HANDLERS ---
  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    const file = e.dataTransfer.files[0];
    if (file) processFile(file);
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) processFile(file);
  };

  const processFile = (file: File) => {
    if (!file.name.endsWith('.json')) {
      setStatus('error');
      setErrorMessage('Please upload the govwatch_data.json file (JSON format).');
      return;
    }

    setStatus('processing');
    const reader = new FileReader();

    reader.onload = async (e) => {
      try {
        const text = e.target?.result as string;
        const rawData = JSON.parse(text);

        if (!Array.isArray(rawData)) {
          throw new Error("File content is not an array");
        }

        // NORMALIZE BEFORE SAVING
        const cleanData: Record[] = [];
        rawData.forEach((item: any, index: number) => {
             // Skip Header Rows 
             if (item['__EMPTY'] === 'TAJUK SEBUT HARGA' || item['__EMPTY_3'] === 'KEMENTERIAN') return;

             // Map fields
             const ministry = item.ministry || item['__EMPTY_3'] || "Unknown Ministry";
             const vendor = item.vendor || item['__EMPTY_4'] || "Unknown Vendor";

             let amount = 0;
             if (typeof item.amount === 'number') amount = item.amount;
             else if (item['__EMPTY_8']) {
                 amount = parseFloat(String(item['__EMPTY_8']).replace(/[^0-9.-]+/g, ""));
             } else if (typeof item.amount === 'string') {
                 amount = parseFloat(item.amount.replace(/[^0-9.-]+/g, ""));
             }

             let date = item.date || item['__EMPTY_6'];
             if (!date || date === 'TIADA MAKLUMAT') date = item['__EMPTY_7'];
             if (!date || date === 'TIADA MAKLUMAT') date = new Date().toISOString().split('T')[0];
             if (date && typeof date === 'string' && date.includes(' ')) date = date.split(' ')[0];

             const category = item.category || item['__EMPTY_2'] || "General";
             const method = item.method || "Open Tender";

             if (ministry !== "Unknown Ministry" || amount > 0) {
                 cleanData.push({
                     id: index + 1,
                     ministry: String(ministry).trim(),
                     vendor: String(vendor).trim(),
                     amount: amount || 0,
                     method: String(method).trim(),
                     category: String(category).trim(),
                     date: String(date).trim(),
                     reason: item.reason || null
                 });
             }
        });

        const response = await fetch('/api/update-data', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(cleanData)
        });

        if (!response.ok) throw new Error('Server failed to save data');

        setStatus('success');
        setTimeout(() => { onDataLoaded(cleanData); }, 1500);

      } catch (err) {
        setStatus('error');
        setErrorMessage('Failed to process or save file.');
        console.error(err);
      }
    };
    reader.readAsText(file);
  };

  // --- AUTO SCRAPE HANDLER ---
  const handleAutoScrape = async () => {
    setScrapeStatus('running');
    try {
        const response = await fetch('/api/trigger-scrape', { method: 'POST' });
        const result = await response.json();

        if (!response.ok) throw new Error(result.error || 'Scrape failed');

        setScrapeStatus('success');

        // Reload data after a short delay
        setTimeout(() => {
             window.location.reload(); 
        }, 2000);

    } catch (err) {
        setScrapeStatus('error');
        console.error(err);
    }
  };

  // 1. LOGIN SCREEN
  if (!isAuthenticated) {
    return (
        <div className="flex flex-col items-center justify-center min-h-[50vh] animate-fadeIn">
            <div className="bg-gw-card p-8 rounded-lg border border-gw-border shadow-2xl max-w-sm w-full">
                <div className="flex justify-center mb-6">
                    <div className="p-4 bg-gw-bg rounded-full border border-gw-border">
                        <Lock className="w-8 h-8 text-gw-muted" />
                    </div>
                </div>
                <h2 className="text-xl font-bold text-white text-center mb-6">Restricted Access</h2>
                <form onSubmit={handleLogin}>
                    <input 
                        type="password" 
                        value={pin}
                        onChange={(e) => setPin(e.target.value)}
                        placeholder="Enter Admin PIN"
                        className="w-full bg-gw-bg border border-gw-border text-center text-white p-3 rounded mb-4 focus:border-gw-success focus:outline-none tracking-widest"
                        autoFocus
                    />
                    <button 
                        type="submit"
                        className="w-full bg-gw-success text-gw-bg font-bold py-3 rounded hover:bg-gw-success/90 transition-colors"
                    >
                        Unlock
                    </button>
                </form>
            </div>
        </div>
    );
  }

  // 2. UPLOAD SCREEN (Authenticated)
  return (
    <div className="max-w-6xl mx-auto animate-fadeIn pb-12">
      <div className="text-center mb-8">
        <div className="inline-block px-3 py-1 rounded-full bg-gw-success/10 text-gw-success text-xs font-bold mb-4 border border-gw-success/20">
            ADMIN MODE ACTIVE
        </div>
        <h1 className="text-3xl font-bold text-white mb-2">Update Database</h1>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">

        {/* OPTION A: AUTO UPDATE (NEW) */}
        <div className="bg-gw-card border border-gw-border rounded-xl p-6 flex flex-col relative overflow-hidden">
            <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-gw-success to-blue-500"></div>
            <h3 className="text-lg font-bold text-white mb-4 flex items-center gap-2">
                <Server className="w-5 h-5 text-gw-success" /> 
                Auto-Update
            </h3>
            <p className="text-sm text-gw-muted mb-6">
                Instruct the server to visit the government website, scrape the latest data, and update the database automatically.
            </p>

            <div className="mt-auto">
                {scrapeStatus === 'idle' && (
                    <button 
                        onClick={handleAutoScrape}
                        className="w-full py-4 bg-gw-success hover:bg-gw-success/90 text-gw-bg font-bold rounded-lg transition-all flex items-center justify-center gap-2"
                    >
