import React, { useState, useMemo } from 'react';
import { Record as ProcurementRecord } from '../types';
import { StatCard } from '../components/StatCard';
import { formatMoney, translateMinistry } from '../utils';
import { ArrowUpRight, Search, FileText, RefreshCw, Filter, ArrowUpDown } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Cell, PieChart, Pie, Legend } from 'recharts';

interface DashboardProps {
  records: ProcurementRecord[];
  isLoading?: boolean;
  onMinistryClick: (name: string) => void;
}

const CustomTooltip = ({ active, payload, label }: any) => {
  if (active && payload && payload.length) {
    return (
      <div className="bg-gw-card border border-gw-border p-3 rounded shadow-xl z-50">
        <p className="text-gw-text font-bold text-sm mb-1">{payload[0].name || label}</p>
        <p className="text-gw-success text-sm font-mono">{formatMoney(payload[0].value as number)}</p>
      </div>
    );
  }
  return null;
};

export const Dashboard: React.FC<DashboardProps> = ({ records, isLoading, onMinistryClick }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState<'date' | 'value'>('date');
  const [filterCategory, setFilterCategory] = useState<string>('All');

  // LOADING
  if (isLoading) {
    return (
        <div className="flex flex-col items-center justify-center min-h-[60vh] text-center animate-fadeIn">
            <RefreshCw className="w-10 h-10 text-gw-success animate-spin mb-4" />
            <h2 className="text-xl font-bold text-white">Loading Dashboard...</h2>
        </div>
    );
  }

  // EMPTY
  if (records.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center min-h-[60vh] text-center animate-fadeIn p-4">
        <div className="bg-gw-card border border-gw-border rounded-full p-6 mb-6">
          <FileText className="w-12 h-12 text-gw-muted" />
        </div>
        <h2 className="text-2xl font-bold text-white mb-2">No Records Available</h2>
        <p className="text-gw-muted max-w-md mb-8">
          The database is currently empty. Please update data via the Admin Panel.
        </p>
      </div>
    );
  }

  // --- KPI LOGIC ---
  const totalSpend = records.reduce((acc, r) => acc + (r.amount || 0), 0);

  // Use Category for stats since "Method" is often vague in raw data
  const suppliesSpend = records
    .filter(r => (r.category || '').toLowerCase().includes('bekalan'))
    .reduce((acc, r) => acc + (r.amount || 0), 0);

  const worksSpend = records
    .filter(r => (r.category || '').toLowerCase().includes('kerja'))
    .reduce((acc, r) => acc + (r.amount || 0), 0);

  const servicesSpend = records
    .filter(r => (r.category || '').toLowerCase().includes('perkhidmatan'))
    .reduce((acc, r) => acc + (r.amount || 0), 0);

  // --- CHART DATA ---

  // 1. Pie Chart (Category Distribution)
  const categoryData = [
    { name: 'Works (Kerja)', value: worksSpend, color: '#ffadad' }, 
    { name: 'Supplies (Bekalan)', value: suppliesSpend, color: '#36d399' }, 
    { name: 'Services (Perkhidmatan)', value: servicesSpend, color: '#8da2ce' }, 
  ].filter(d => d.value > 0);

  // 2. Bar Chart (Top Ministries)
  const ministryTotals = records.reduce((acc: { [key: string]: number }, curr) => {
    if (!curr.ministry) return acc;
    // Translate the key for the chart
    const englishName = translateMinistry(curr.ministry);
    acc[englishName] = (acc[englishName] || 0) + (curr.amount || 0);
    return acc;
  }, {});

  const barChartData = Object.entries(ministryTotals)
    .map(([name, value]) => ({ name, value }))
    .sort((a, b) => b.value - a.value)
    .slice(0, 8); // Top 8 only

  // --- TABLE FILTERING ---
  const filteredRecords = useMemo(() => {
    return records.filter(r => {
      const ministryRaw = (r.ministry || '').toLowerCase();
      const ministryEn = translateMinistry(r.ministry).toLowerCase();
      const vendor = (r.vendor || '').toLowerCase();
      const search = searchTerm.toLowerCase();

      // Search matches either raw Malay name, Translated English name, or Vendor
      const matchesSearch = ministryRaw.includes(search) || ministryEn.includes(search) || vendor.includes(search);

      const matchesCategory = filterCategory === 'All' || (r.category || '').includes(filterCategory);

      return matchesSearch && matchesCategory;
    }).sort((a,b) => {
      if (sortBy === 'value') return b.amount - a.amount;
      // Default date sort
      return new Date(b.date).getTime() - new Date(a.date).getTime();
    });
  }, [records, searchTerm, filterCategory, sortBy]);

  return (
    <div className="space-y-8 animate-fadeIn pb-12">

      {/* KPI Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <StatCard label="Total Contract Value" value={formatMoney(totalSpend)} />
        <StatCard label="Works & Construction" value={formatMoney(worksSpend)} isAlert />
        <StatCard label="Supplies & Services" value={formatMoney(suppliesSpend + servicesSpend)} />
      </div>

      {/* Charts Row */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 min-h-[400px]">
         {/* Bar Chart (Horizontal) */}
         <div className="lg:col-span-2 bg-gw-card border border-gw-border rounded-lg p-6 flex flex-col">
            <h3 className="text-lg font-bold mb-4 text-white">Top Spending Ministries</h3>
            <div className="flex-1">
                <ResponsiveContainer width="100%" height="100%">
                    <BarChart data={barChartData} layout="vertical" margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
                        <XAxis type="number" hide />
                        <YAxis 
                            dataKey="name" 
                            type="category" 
                            width={180} 
                            tick={{ fill: '#e5e7ef', fontSize: 10, width: 170 }}
                            interval={0}
                        />
                        <Tooltip cursor={{fill: '#223055'}} content={<CustomTooltip />} />
                        <Bar dataKey="value" radius={[0, 4, 4, 0]}>
                            {barChartData.map((entry, index) => (
                                <Cell key={`cell-${index}`} fill={index === 0 ? '#ffadad' : '#36d399'} />
                            ))}
                        </Bar>
                    </BarChart>
                </ResponsiveContainer>
            </div>
         </div>

         {/* Pie Chart */}
         <div className="bg-gw-card border border-gw-border rounded-lg p-6 flex flex-col">
            <h3 className="text-lg font-bold mb-4 text-white">Category Breakdown</h3>
            <div className="flex-1">
                <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                        <Pie
                            data={categoryData}
                            cx="50%"
                            cy="50%"
                            innerRadius={60}
                            outerRadius={80}
                            paddingAngle={5}
                            dataKey="value"
                        >
                            {categoryData.map((entry, index) => (
                                <Cell key={`cell-${index}`} fill={entry.color} stroke="none" />
                            ))}
                        </Pie>
                        <Tooltip content={<CustomTooltip />} />
                        <Legend verticalAlign="bottom" height={36} iconType="circle" />
                    </PieChart>
                </ResponsiveContainer>
            </div>
         </div>
      </div>

      {/* Table Section */}
      <div className="bg-gw-card border border-gw-border rounded-lg overflow-hidden flex flex-col">
          {/* Table Controls */}
          <div className="p-4 border-b border-gw-border flex flex-col md:flex-row justify-between items-center gap-4 bg-gw-bg/30">
            <div className="flex items-center gap-2 w-full md:w-auto">
                 <h2 className="text-lg font-bold text-white flex items-center gap-2 mr-4">
                    <FileText className="w-5 h-5" /> Recent Awards
                 </h2>
                 {/* Sort Dropdown */}
                 <div className="flex items-center bg-gw-bg border border-gw-border rounded px-2 py-1">
                    <ArrowUpDown className="w-4 h-4 text-gw-muted mr-2" />
                    <select 
                        value={sortBy} 
                        onChange={(e) => setSortBy(e.target.value as any)}
                        className="bg-transparent text-sm text-gw-text focus:outline-none"
                    >
                        <option value="date">Newest First</option>
                        <option value="value">Highest Value</option>
                    </select>
                 </div>
                 {/* Filter Dropdown */}
                 <div className="flex items-center bg-gw-bg border border-gw-border rounded px-2 py-1 ml-2">
                    <Filter className="w-4 h-4 text-gw-muted mr-2" />
                    <select 
                        value={filterCategory} 
                        onChange={(e) => setFilterCategory(e.target.value)}
                        className="bg-transparent text-sm text-gw-text focus:outline-none"
                    >
                        <option value="All">All Categories</option>
                        <option value="Kerja">Works (Kerja)</option>
                        <option value="Bekalan">Supplies (Bekalan)</option>
                        <option value="Perkhidmatan">Services (Perkhidmatan)</option>
                    </select>
                 </div>
            </div>

            <div className="relative w-full md:w-64">
                <Search className="w-4 h-4 absolute left-3 top-1/2 -translate-y-1/2 text-gw-muted" />
                <input 
                    type="text" 
                    placeholder="Search records..." 
                    className="w-full bg-gw-bg border border-gw-border text-sm rounded-full pl-10 pr-4 py-2 text-gw-text focus:outline-none focus:border-gw-success"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>
          </div>

          <div className="overflow-x-auto">
            <table className="w-full text-left text-sm">
              <thead className="bg-gw-bg/95">
                <tr>
                  <th className="px-6 py-4 font-bold text-gw-muted uppercase text-xs tracking-wider">Date</th>
                  <th className="px-6 py-4 font-bold text-gw-muted uppercase text-xs tracking-wider">Ministry</th>
                  <th className="px-6 py-4 font-bold text-gw-muted uppercase text-xs tracking-wider">Vendor</th>
                  <th className="px-6 py-4 font-bold text-gw-muted uppercase text-xs tracking-wider text-right">Value</th>
                  <th className="px-6 py-4 font-bold text-gw-muted uppercase text-xs tracking-wider text-right">Category</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gw-border">
                {filteredRecords.slice(0, 50).map((r) => (
                  <tr key={r.id} className="hover:bg-gw-bg/50 transition-colors group">
                    <td className="px-6 py-4 whitespace-nowrap text-gw-muted font-mono text-xs">{r.date}</td>
                    <td className="px-6 py-4">
                      <button 
                        onClick={() => onMinistryClick(r.ministry)}
                        className="text-white hover:text-gw-success font-medium flex flex-col items-start transition-colors text-left"
                      >
                        <span className="line-clamp-1 max-w-[250px] font-bold" title={r.ministry}>
                            {translateMinistry(r.ministry)}
                        </span>
                        <span className="text-[10px] text-gw-muted flex items-center gap-1">
                            {r.ministry} 
                            <ArrowUpRight className="w-2 h-2 opacity-0 group-hover:opacity-100 transition-opacity" />
                        </span>
                      </button>
                    </td>
                    <td className="px-6 py-4 text-gw-muted truncate max-w-[200px]" title={r.vendor}>{r.vendor}</td>
                    <td className="px-6 py-4 text-right font-bold text-gw-text font-mono">{formatMoney(r.amount)}</td>
                    <td className="px-6 py-4 text-right whitespace-nowrap">
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                        (r.category || '').toLowerCase().includes('kerja')
                          ? 'bg-gw-danger/10 text-gw-danger border border-gw-danger/20'
                          : 'bg-gw-success/10 text-gw-success border border-gw-success/20'
                      }`}>
                        {r.category || 'General'}
                      </span>
                    </td>
                  </tr>
                ))}
                {filteredRecords.length === 0 && (
                    <tr>
                        <td colSpan={5} className="text-center py-8 text-gw-muted italic">No records found matching your filters.</td>
                    </tr>
                )}
              </tbody>
            </table>
          </div>
          <div className="p-4 border-t border-gw-border bg-gw-bg/30 text-center text-xs text-gw-muted">
            Showing top {Math.min(filteredRecords.length, 50)} recent results
          </div>
      </div>
    </div>
  );
};

import React, { useState } from 'react';
import { Record as ProcurementRecord } from '../types';
import { formatMoney, translateMinistry } from '../utils';
import { Building2, ChevronRight, Search } from 'lucide-react';

interface MinistryListProps {
  records: ProcurementRecord[];
  onSelectMinistry: (name: string) => void;
}

export const MinistryList: React.FC<MinistryListProps> = ({ records, onSelectMinistry }) => {
  const [searchTerm, setSearchTerm] = useState('');

  // Aggregate Data
  const ministryStats = records.reduce((acc, curr) => {
    const name = curr.ministry;
    if (!acc[name]) {
      acc[name] = { count: 0, value: 0 };
    }
    acc[name].count += 1;
    acc[name].value += curr.amount;
    return acc;
  }, {} as Record<string, { count: number; value: number }>);

  const sortedMinistries = Object.entries(ministryStats)
    .map(([name, stats]) => ({ 
        name, // The raw malay name (for ID)
        englishName: translateMinistry(name), // For display
        ...stats 
    }))
    .sort((a, b) => b.value - a.value)
    .filter(m => 
        m.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
        m.englishName.toLowerCase().includes(searchTerm.toLowerCase())
    );

  return (
    <div className="animate-fadeIn space-y-6">
      <div className="flex flex-col md:flex-row justify-between items-center gap-4">
        <div>
          <h1 className="text-2xl font-bold text-white flex items-center gap-2">
            <Building2 className="w-8 h-8 text-gw-success" />
            Ministries & Agencies
          </h1>
          <p className="text-gw-muted mt-1">Breakdown of spending by government body.</p>
        </div>
        <div className="relative">
             <Search className="w-4 h-4 absolute left-3 top-1/2 -translate-y-1/2 text-gw-muted" />
             <input 
                type="text" 
                placeholder="Find a ministry..." 
                className="bg-gw-card border border-gw-border rounded-full pl-10 pr-4 py-2 text-sm text-gw-text focus:border-gw-success focus:outline-none w-64 shadow-sm"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
             />
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {sortedMinistries.map((m) => (
          <div 
            key={m.name}
            onClick={() => onSelectMinistry(m.name)}
            className="bg-gw-card border border-gw-border rounded-lg p-5 hover:border-gw-success/50 hover:bg-gw-card/80 transition-all cursor-pointer group flex flex-col justify-between"
          >
            <div>
              <h3 className="font-bold text-white line-clamp-2 mb-1 group-hover:text-gw-success transition-colors">
                {m.englishName}
              </h3>
              <p className="text-xs text-gw-muted/70 uppercase tracking-wider mb-2 line-clamp-1">{m.name}</p>

              <div className="flex items-center gap-2 text-xs text-gw-muted">
                 <span className="px-2 py-1 bg-gw-bg rounded border border-gw-border">{m.count} Contracts</span>
              </div>
            </div>
            <div className="mt-4 pt-4 border-t border-gw-border flex justify-between items-end">
                <div>
                    <span className="text-xs text-gw-muted uppercase tracking-wider">Total Spend</span>
                    <div className="text-lg font-bold text-white">{formatMoney(m.value)}</div>
                </div>
                <ChevronRight className="w-5 h-5 text-gw-muted group-hover:translate-x-1 transition-transform" />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

import React from 'react';
import { Record } from '../types';
import { formatMoney, translateMinistry } from '../utils';
import { ArrowLeft, Building2, Calendar, CreditCard, FileWarning } from 'lucide-react';

interface MinistryDetailProps {
  ministryName: string;
  records: Record[];
  onBack: () => void;
}

export const MinistryDetail: React.FC<MinistryDetailProps> = ({ ministryName, records, onBack }) => {
  const filteredRecords = records.filter(r => r.ministry === ministryName).sort((a,b) => new Date(b.date).getTime() - new Date(a.date).getTime());
  const totalSpend = filteredRecords.reduce((acc, r) => acc + r.amount, 0);

  return (
    <div className="space-y-6 animate-fadeIn">
      <button 
        onClick={onBack}
        className="flex items-center text-gw-muted hover:text-gw-text transition-colors mb-4 group"
      >
        <ArrowLeft className="w-4 h-4 mr-2 group-hover:-translate-x-1 transition-transform" />
        Back to Dashboard
      </button>

      <div className="bg-gw-card border border-gw-border rounded-lg p-8 shadow-lg">
        <div className="flex flex-col md:flex-row md:items-end justify-between gap-4 border-b border-gw-border pb-6 mb-6">
            <div>
                <div className="flex items-center gap-3 mb-2">
                    <div className="p-2 bg-gw-bg rounded border border-gw-border">
                        <Building2 className="w-6 h-6 text-gw-muted" />
                    </div>
                    <span className="text-gw-muted uppercase tracking-wider text-sm font-semibold">Ministry Report</span>
                </div>
                <h1 className="text-3xl font-bold text-white">{translateMinistry(ministryName)}</h1>
                <p className="text-gw-muted text-sm mt-1">{ministryName}</p>
            </div>
            <div className="text-left md:text-right">
                <div className="text-sm text-gw-muted mb-1">Total Contract Value</div>
                <div className="text-3xl font-bold text-gw-success">{formatMoney(totalSpend)}</div>
            </div>
        </div>

        <div className="grid grid-cols-1 gap-4">
            {filteredRecords.map((r) => (
                <div key={r.id} className="bg-gw-bg border border-gw-border rounded-lg p-5 hover:border-gw-muted/50 transition-colors">
                    <div className="flex flex-col md:flex-row justify-between md:items-start gap-4 mb-4">
                        <div className="flex-1">
                            <h3 className="text-lg font-bold text-white mb-1">{r.vendor}</h3>
                            <div className="flex items-center gap-4 text-sm text-gw-muted">
                                <span className="flex items-center gap-1">
                                    <Calendar className="w-3 h-3" /> {r.date}
                                </span>
                                <span className="flex items-center gap-1">
                                    <CreditCard className="w-3 h-3" /> {r.method}
                                </span>
                            </div>
                        </div>
                        <div className={`text-xl font-bold ${r.method.includes('Direct') ? 'text-gw-danger' : 'text-gw-success'}`}>
                            {formatMoney(r.amount)}
                        </div>
                    </div>

                    {r.reason ? (
                        <div className="bg-gw-card/50 rounded p-3 text-sm text-gw-text border border-gw-border/50 flex gap-2 items-start">
                             <FileWarning className="w-4 h-4 text-gw-danger shrink-0 mt-0.5" />
                             <div>
                                <span className="text-gw-danger font-semibold uppercase text-xs tracking-wide block mb-1">Direct Negotiation Justification</span>
                                {r.reason}
                             </div>
                        </div>
                    ) : (
                        <div className="text-xs text-gw-muted italic opacity-50">Standard procurement via open tender process.</div>
                    )}
                </div>
            ))}
        </div>
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Layout } from './components/Layout';
import { Dashboard } from './views/Dashboard';
import { MinistryDetail } from './views/MinistryDetail';
import { MinistryList } from './views/MinistryList';
import { VendorList } from './views/VendorList';
import { Upload } from './views/Upload';
import { About } from './views/About';
import { INITIAL_RECORDS } from './data';
import { ViewConfig, Record } from './types';

function App() {
  const [viewConfig, setViewConfig] = useState<ViewConfig>({ view: 'dashboard' });
  const [records, setRecords] = useState<Record[]>(INITIAL_RECORDS);
  const [isLoading, setIsLoading] = useState(true);

  // Auto-fetch data on startup
  const fetchData = async () => {
    try {
      const response = await fetch('/data.json');
      if (response.ok) {
        const rawData = await response.json();

        // NORMALIZE DATA
        const cleanData: Record[] = [];
        if (Array.isArray(rawData)) {
             rawData.forEach((item: any, index: number) => {
                // 1. Skip Header Rows
                if (item['__EMPTY'] === 'TAJUK SEBUT HARGA' || item['__EMPTY_3'] === 'KEMENTERIAN') return;

                // 2. Map fields
                const ministry = item.ministry || item['__EMPTY_3'] || "Unknown Ministry";
                const vendor = item.vendor || item['__EMPTY_4'] || "Unknown Vendor";

                // Parse Amount
                let amount = 0;
                if (typeof item.amount === 'number') amount = item.amount;
                else if (item['__EMPTY_8']) {
                    amount = parseFloat(String(item['__EMPTY_8']).replace(/[^0-9.-]+/g, ""));
                } else if (typeof item.amount === 'string') {
                    amount = parseFloat(item.amount.replace(/[^0-9.-]+/g, ""));
                }

                // Parse Date
                let date = item.date || item['__EMPTY_6'];
                if (!date || date === 'TIADA MAKLUMAT') date = item['__EMPTY_7'];
                if (!date || date === 'TIADA MAKLUMAT') date = new Date().toISOString().split('T')[0];
                if (date && typeof date === 'string' && date.includes(' ')) {
                    date = date.split(' ')[0];
                }

                // Map Category & Method
                const category = item.category || item['__EMPTY_2'] || "General";
                const method = item.method || "Open Tender";

                if (ministry !== "Unknown Ministry" || amount > 0) {
                    cleanData.push({
                        id: index + 1,
                        ministry: String(ministry).trim(),
                        vendor: String(vendor).trim(),
                        amount: amount || 0,
                        method: String(method).trim(),
                        category: String(category).trim(),
                        date: String(date).trim(),
                        reason: item.reason || null
                    });
                }
             });
        }

        if (cleanData.length > 0) {
          setRecords(cleanData);
        }
      }
    } catch (error) {
      console.warn('Network error checking for data:', error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchData();

    const checkHash = () => {
      if (window.location.hash === '#secret-admin-panel') {
        setViewConfig({ view: 'upload' });
      }
    };

    checkHash();
    window.addEventListener('hashchange', checkHash);
    return () => window.removeEventListener('hashchange', checkHash);
  }, []);

  const handleNavigate = (view: any) => {
    setViewConfig({ view });
    window.scrollTo(0, 0);
    if (view === 'dashboard' && window.location.hash === '#secret-admin-panel') {
        history.pushState("", document.title, window.location.pathname + window.location.search);
    }
  };

  const handleMinistryClick = (ministryName: string) => {
    setViewConfig({ view: 'ministry_detail', ministryName });
    window.scrollTo(0, 0);
  };

  const handleDataLoaded = (newRecords: Record[]) => {
    setRecords(newRecords);
    handleNavigate('dashboard');
  };

  return (
    <Layout activeView={viewConfig.view} onNavigate={handleNavigate}>
      {viewConfig.view === 'dashboard' && (
        <Dashboard 
          records={records} 
          isLoading={isLoading}
          onMinistryClick={handleMinistryClick} 
        />
      )}

      {viewConfig.view === 'ministry_detail' && viewConfig.ministryName && (
        <MinistryDetail 
          ministryName={viewConfig.ministryName} 
          records={records}
          onBack={() => handleNavigate('ministry_list')}
        />
      )}

      {viewConfig.view === 'ministry_list' && (
        <MinistryList 
            records={records} 
            onSelectMinistry={handleMinistryClick} 
        />
      )}

      {viewConfig.view === 'vendor_list' && (
        <VendorList records={records} />
      )}

      {viewConfig.view === 'upload' && (
        <Upload onDataLoaded={handleDataLoaded} />
      )}

      {viewConfig.view === 'about' && (
        <About />
      )}
    </Layout>
  );
}

export default App;

import React, { useState, useMemo } from 'react';
import { Record as ProcurementRecord } from '../types';
import { StatCard } from '../components/StatCard';
