import React from 'react';
import { Record } from '../types';
import { formatMoney, translateMinistry } from '../utils';
import { ArrowLeft, Building2, Calendar, CreditCard, FileWarning, AlertTriangle, PieChart as PieChartIcon, TrendingUp } from 'lucide-react';
import { PieChart, Pie, Cell, Tooltip, ResponsiveContainer } from 'recharts';

interface MinistryDetailProps {
  ministryName: string;
  records: Record[];
  onBack: () => void;
}

export const MinistryDetail: React.FC<MinistryDetailProps> = ({ ministryName, records, onBack }) => {
  const filteredRecords = records.filter(r => r.ministry === ministryName).sort((a,b) => new Date(b.date).getTime() - new Date(a.date).getTime());
  const totalSpend = filteredRecords.reduce((acc, r) => acc + r.amount, 0);

  // --- ANALYSIS LOGIC ---

  // 1. Procurement Method Analysis (Integrity Indicator)
  const methodStats = filteredRecords.reduce((acc, r) => {
    // Basic heuristic: check string for "Rundingan Terus" (Direct Negotiation)
    const isDirect = r.method.toLowerCase().includes('rundingan') || r.method.toLowerCase().includes('direct');
    if (isDirect) {
        acc.directCount += 1;
        acc.directValue += r.amount;
    } else {
        acc.openCount += 1;
        acc.openValue += r.amount;
    }
    return acc;
  }, { directCount: 0, directValue: 0, openCount: 0, openValue: 0 });

  const totalCount = methodStats.directCount + methodStats.openCount;
  const directPercent = totalCount > 0 ? (methodStats.directCount / totalCount) * 100 : 0;

  // 2. Vendor Concentration (Market Dominance)
  const vendorStats = filteredRecords.reduce((acc, r) => {
    acc[r.vendor] = (acc[r.vendor] || 0) + r.amount;
    return acc;
  }, {} as { [key: string]: number });

  // Fix: Cast Object.entries to ensure values are numbers, avoiding arithmetic on 'unknown'
  const topVendors = (Object.entries(vendorStats) as [string, number][])
    .sort((a, b) => b[1] - a[1])
    .slice(0, 5)
    .map(([name, value]) => ({
        name,
        value,
        percent: (value / totalSpend) * 100
    }));

  return (
    <div className="space-y-6 animate-fadeIn pb-12">
      <button 
        onClick={onBack}
        className="flex items-center text-gw-muted hover:text-gw-text transition-colors mb-4 group"
      >
        <ArrowLeft className="w-4 h-4 mr-2 group-hover:-translate-x-1 transition-transform" />
        Back to Dashboard
      </button>

      {/* HEADER CARD */}
      <div className="bg-gw-card border border-gw-border rounded-lg p-8 shadow-lg">
        <div className="flex flex-col md:flex-row md:items-end justify-between gap-4">
            <div>
                <div className="flex items-center gap-3 mb-2">
                    <div className="p-2 bg-gw-bg rounded border border-gw-border">
                        <Building2 className="w-6 h-6 text-gw-muted" />
                    </div>
                    <span className="text-gw-muted uppercase tracking-wider text-sm font-semibold">Ministry Profile</span>
                </div>
                <h1 className="text-3xl font-bold text-white">{translateMinistry(ministryName)}</h1>
                <p className="text-gw-muted text-sm mt-1">{ministryName}</p>
            </div>
            <div className="text-left md:text-right">
                <div className="text-sm text-gw-muted mb-1">Total Contract Value</div>
                <div className="text-3xl font-bold text-gw-success">{formatMoney(totalSpend)}</div>
                <div className="text-xs text-gw-muted mt-1">{filteredRecords.length} Total Contracts</div>
            </div>
        </div>
      </div>

      {/* ANALYTICS ROW (OpenTender Style) */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">

          {/* 1. INTEGRITY / RISK PROFILE */}
          <div className="bg-gw-card border border-gw-border rounded-lg p-6">
             <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-bold text-white flex items-center gap-2">
                    <AlertTriangle className="w-5 h-5 text-gw-danger" />
                    Procurement Integrity
                </h3>
             </div>

             <div className="space-y-6">
                <div>
                    <div className="flex justify-between text-sm mb-2">
                        <span className="text-gw-muted">Method Breakdown (by count)</span>
                        <span className={`font-bold ${directPercent > 20 ? 'text-gw-danger' : 'text-gw-success'}`}>
                            {directPercent.toFixed(1)}% Direct Negotiation
                        </span>
                    </div>
                    {/* Visual Bar */}
                    <div className="h-4 w-full bg-gw-bg rounded-full overflow-hidden flex border border-gw-border">
                        <div 
                            className="h-full bg-gw-danger transition-all duration-500" 
                            style={{ width: `${directPercent}%` }}
                            title="Direct Negotiation"
                        ></div>
                        <div 
                            className="h-full bg-gw-success transition-all duration-500" 
                            style={{ width: `${100 - directPercent}%` }}
                            title="Open Tender / Quotation"
                        ></div>
                    </div>
                    <div className="flex justify-between text-xs text-gw-muted mt-2">
                        <span className="flex items-center gap-1"><div className="w-2 h-2 rounded-full bg-gw-danger"></div> Direct ({methodStats.directCount})</span>
                        <span className="flex items-center gap-1"><div className="w-2 h-2 rounded-full bg-gw-success"></div> Open/Quote ({methodStats.openCount})</span>
                    </div>
                </div>

                <div className="bg-gw-bg/50 p-4 rounded border border-gw-border">
                    <h4 className="text-xs font-bold text-gw-muted uppercase mb-2">Risk Analysis</h4>
                    <p className="text-sm text-gw-text">
                        {directPercent > 30 
                            ? "High risk of limited competition. A significant portion of contracts are awarded via Direct Negotiation."
                            : "Healthy procurement mix. Most contracts appear to be competitive."}
                    </p>
                </div>
             </div>
          </div>

          {/* 2. MARKET CONCENTRATION (TOP VENDORS) */}
          <div className="bg-gw-card border border-gw-border rounded-lg p-6">
             <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-bold text-white flex items-center gap-2">
                    <PieChartIcon className="w-5 h-5 text-blue-400" />
                    Vendor Dominance
                </h3>
             </div>

             <div className="space-y-4">
                 {topVendors.map((v, i) => (
                     <div key={i} className="group">
                         <div className="flex justify-between items-center text-sm mb-1">
                             <span className="truncate max-w-[200px] text-white group-hover:text-blue-400 transition-colors" title={v.name}>
                                 {i+1}. {v.name}
                             </span>
                             <span className="font-mono text-gw-muted">{formatMoney(v.value)}</span>
                         </div>
                         <div className="w-full bg-gw-bg rounded-full h-1.5">
                             <div 
                                className="bg-blue-500 h-1.5 rounded-full" 
                                style={{ width: `${v.percent}%` }}
                             ></div>
                         </div>
                         <div className="text-[10px] text-right text-gw-muted mt-0.5">
                             {v.percent.toFixed(1)}% of total spend
                         </div>
                     </div>
                 ))}
             </div>
          </div>
      </div>

      {/* DETAILED LIST */}
      <div>
        <h3 className="text-xl font-bold text-white mb-4">Contract History</h3>
        <div className="grid grid-cols-1 gap-4">
            {filteredRecords.map((r) => (
                <div key={r.id} className="bg-gw-card border border-gw-border rounded-lg p-5 hover:border-gw-muted/50 transition-colors">
                    <div className="flex flex-col md:flex-row justify-between md:items-start gap-4 mb-4">
                        <div className="flex-1">
                            <h3 className="text-lg font-bold text-white mb-1">{r.vendor}</h3>
                            <div className="flex items-center gap-4 text-sm text-gw-muted">
                                <span className="flex items-center gap-1">
                                    <Calendar className="w-3 h-3" /> {r.date}
                                </span>
                                <span className="flex items-center gap-1">
                                    <CreditCard className="w-3 h-3" /> {r.method}
                                </span>
                            </div>
                        </div>
                        <div className={`text-xl font-bold ${r.method.toLowerCase().includes('rundingan') || r.method.toLowerCase().includes('direct') ? 'text-gw-danger' : 'text-gw-success'}`}>
                            {formatMoney(r.amount)}
                        </div>
                    </div>

                    {r.reason ? (
                        <div className="bg-gw-danger/5 rounded p-3 text-sm text-gw-text border border-gw-danger/20 flex gap-2 items-start">
                             <FileWarning className="w-4 h-4 text-gw-danger shrink-0 mt-0.5" />
                             <div>
                                <span className="text-gw-danger font-semibold uppercase text-xs tracking-wide block mb-1">Direct Negotiation Justification</span>
                                {r.reason}
                             </div>
                        </div>
                    ) : (
                        <div className="text-xs text-gw-muted italic opacity-50">Standard procurement via open tender process.</div>
                    )}
                </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export interface Record {
  id: number;
  ministry: string;
  vendor: string;
  amount: number;
  method: string;
  category: string; // New field for 'Kerja', 'Bekalan', etc.
  date: string;
  reason?: string | null;
  // OpenTender-like Metadata
  sourceUrl?: string; // Link to the original listing
  crawledAt?: string; // ISO Date string of when we fetched this
}

export type ViewState = 'dashboard' | 'upload' | 'ministry_detail' | 'ministry_list' | 'vendor_list' | 'about';

export interface ViewConfig {
  view: ViewState;
  ministryName?: string;
}

/**
 * GOVWATCH DATA SCRAPER
 * ---------------------
 * Instructions:
 * 1. Open https://myprocurement.treasury.gov.my/results/tender in your browser.
 * 2. Ensure the table is visible (you may need to select "Show All" or paginate).
 * 3. Open Developer Tools (Press F12 or Right Click > Inspect > Console).
 * 4. Paste this entire script into the Console and press Enter.
 * 5. The data will download as 'govwatch_data.json'.
 * 6. Upload this file to the GovWatch App.
 */

(function() {
  const rows = document.querySelectorAll('table tbody tr');
  const data = [];
  const currentUrl = window.location.href;
  const now = new Date().toISOString();

  // Helper to clean currency strings (e.g., "RM 1,234.50" -> 1234.50)
  const parseAmount = (str) => {
    if (!str) return 0;
    return parseFloat(str.replace(/[^0-9.-]+/g, ""));
  };

  console.log(`Found ${rows.length} rows. Processing...`);

  rows.forEach((row, index) => {
    const cols = row.querySelectorAll('td');
    if (cols.length < 4) return;

    // NOTE: Adjust indices [0], [1], etc based on the specific column order of the target website table
    // Assuming standard layout: Date | Ministry | Vendor | Amount | Method
    // You may need to inspect the table headers to match these indices.

    const record = {
      id: index + 1,
      // Fallback logic tries to grab text safely
      date: cols[0]?.innerText?.trim() || new Date().toISOString().split('T')[0],
      ministry: cols[1]?.innerText?.trim() || "Unknown Ministry",
      vendor: cols[2]?.innerText?.trim() || "Unknown Vendor",
      amount: parseAmount(cols[3]?.innerText), 
      method: cols[4]?.innerText?.trim() || "Open Tender", // Default if missing
      reason: null,
      sourceUrl: currentUrl,
      crawledAt: now
    };

    data.push(record);
  });

  const jsonString = JSON.stringify(data, null, 2);
  const blob = new Blob([jsonString], { type: "application/json" });
  const url = URL.createObjectURL(blob);

  const a = document.createElement('a');
  a.href = url;
  a.download = 'govwatch_data.json';
  document.body.appendChild(a);
  a.click();
  document.body.removeChild(a);
  URL.revokeObjectURL(url);

  console.log(`Successfully scraped ${data.length} records! File downloading...`);
})();

import puppeteer from 'puppeteer';
import fs from 'fs/promises';
import path from 'path';
import { fileURLToPath } from 'url';

const __dirname = path.dirname(fileURLToPath(import.meta.url));

// Configuration
const TARGET_URL = 'https://myprocurement.treasury.gov.my/results/tender';
const OUTPUT_PATH = path.resolve(__dirname, '../public/data.json');

async function scrape() {
  console.log(`[${new Date().toISOString()}] Starting scraping job...`);

  let browser;
  try {
    // LAUNCH BROWSER IN STEALTH MODE
    browser = await puppeteer.launch({
      headless: "new", 
      executablePath: process.env.PUPPETEER_EXECUTABLE_PATH || undefined,
      args: [
          '--no-sandbox', 
          '--disable-setuid-sandbox',
          '--disable-dev-shm-usage', 
          '--single-process',
          '--disable-blink-features=AutomationControlled', // CRITICAL: Hides "controlled by automation" flag
          '--window-size=1920,1080',
          '--disable-infobars',
          '--disable-features=IsolateOrigins,site-per-process' 
      ],
      ignoreDefaultArgs: ['--enable-automation']
    });

    const page = await browser.newPage();

    // 1. Extra Stealth: Mask WebDriver property
    await page.evaluateOnNewDocument(() => {
      Object.defineProperty(navigator, 'webdriver', {
        get: () => undefined,
      });
    });

    // 2. Set Realistic Viewport & User Agent
    await page.setViewport({ width: 1920, height: 1080 });
    await page.setUserAgent('Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36');

    console.log(`Navigating to ${TARGET_URL}...`);

    // 3. Navigation with loose requirements (don't wait for networkidle strictly, it can hang on gov sites)
    await page.goto(TARGET_URL, { waitUntil: 'domcontentloaded', timeout: 90000 });

    console.log('Page loaded. Waiting for table data...');

    // 4. Robust Waiting Strategy
    // Wait a fixed time for JS framework to initialize (SPA behavior)
    await new Promise(r => setTimeout(r, 5000));

    // Wait specifically for the table rows to appear
    try {
        await page.waitForSelector('table tbody tr', { timeout: 45000, visible: true });
    } catch (e) {
        console.error("Timeout waiting for table. Site might be slow or blocking.");
        throw new Error("Table selector not found - site might be blocking or structure changed.");
    }

    let allRecords = [];
    let hasNextPage = true;
    let pageNum = 1;
    const MAX_PAGES = 5; // Safety limit to prevent timeouts during this demo
    const crawlTime = new Date().toISOString();

    while (hasNextPage && pageNum <= MAX_PAGES) {
        console.log(`Scraping page ${pageNum}...`);

        // 5. Data Extraction
        // Using textContent is often faster/safer than innerText in headless
        const pageData = await page.evaluate((targetUrl, time) => {
            const rows = Array.from(document.querySelectorAll('table tbody tr'));
            const results = [];

            rows.forEach((row) => {
                const cols = row.querySelectorAll('td');
                // Check if it's a "No data" row or invalid
                if (cols.length < 4) return; 

                const getText = (col) => col ? col.textContent.trim() : "";

                const dateStr = getText(cols[0]);
                const ministryStr = getText(cols[1]);
                const vendorStr = getText(cols[2]);
                const amountStr = getText(cols[3]);
                const methodStr = getText(cols[4]);

                // Basic validation to ensure we aren't scraping headers
                if (ministryStr === "Ministry" || vendorStr === "Vendor") return;

                const parseAmount = (str) => {
                    if (!str) return 0;
                    return parseFloat(str.replace(/[^0-9.-]+/g, ""));
                };

                results.push({
                    date: dateStr || new Date().toISOString().split('T')[0],
                    ministry: ministryStr || "Unknown",
                    vendor: vendorStr || "Unknown",
                    amount: parseAmount(amountStr),
                    method: methodStr || "Unknown",
                    sourceUrl: targetUrl,
                    crawledAt: time
                });
            });
            return results;
        }, TARGET_URL, crawlTime);

        if (pageData.length > 0) {
            allRecords = [...allRecords, ...pageData];
            console.log(`  Found ${pageData.length} records on page ${pageNum}.`);
        } else {
            console.log(`  No records found on page ${pageNum}.`);
        }

        // 6. Pagination Logic
        // Look for common "Next" buttons. MyProcurement likely uses standard Bootstrap/DataTables.
        // We try multiple selectors to be safe.
        const nextButtonSelectors = [
            'li.next:not(.disabled) a', // Common Bootstrap
            '.paginate_button.next:not(.disabled)', // DataTables
            'button[aria-label="Next"]:not([disabled])', // Material UI / Aria
            'a[rel="next"]' // Standard HTML
        ];

        let nextButton = null;
        for (const selector of nextButtonSelectors) {
            nextButton = await page.$(selector);
            if (nextButton) {
                console.log(`  Found next button with selector: ${selector}`);
                break;
            }
        }

        if (nextButton) {
            try {
                // Scroll into view to ensure it's clickable
                await page.evaluate((btn) => btn.scrollIntoView(), nextButton);

                // Click and wait for table refresh
                await Promise.all([
                   nextButton.click(),
                   new Promise(r => setTimeout(r, 4000)) 
                ]);
                pageNum++;
            } catch (err) {
                console.warn("  Error clicking next button:", err);
                hasNextPage = false;
            }
        } else {
            console.log("  No next button found or disabled. End of list.");
            hasNextPage = false;
        }
    }

    // 7. Save Data
    if (allRecords.length > 0) {
        const finalData = allRecords.map((r, i) => ({ ...r, id: i + 1 }));

        const publicDir = path.dirname(OUTPUT_PATH);
        try { await fs.access(publicDir); } catch { await fs.mkdir(publicDir, { recursive: true }); }

        await fs.writeFile(OUTPUT_PATH, JSON.stringify(finalData, null, 2));
        console.log(`[Success] Scraped total ${finalData.length} records. Saved to data.json`);
    } else {
        console.warn("[Warning] Scraper finished but found 0 records.");
    }

  } catch (error) {
    console.error('[Error] Scraping fatal error:', error);
    throw error; // Propagate error so the API knows it failed
  } finally {
    if (browser) await browser.close();
  }
}

