import { formatMoney, translateMinistry, downloadCSV } from '../utils';
import { ArrowUpRight, Search, FileText, RefreshCw, Filter, ArrowUpDown, Download, Clock, Briefcase } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Cell, PieChart, Pie, Legend } from 'recharts';

interface DashboardProps {
  records: ProcurementRecord[];
  isLoading?: boolean;
  onMinistryClick: (name: string) => void;
}

const CustomTooltip = ({ active, payload, label }: any) => {
  if (active && payload && payload.length) {
    return (
      <div className="bg-gw-card border border-gw-border p-3 rounded shadow-xl z-50">
        <p className="text-gw-text font-bold text-sm mb-1">{payload[0].name || label}</p>
        <p className="text-gw-success text-sm font-mono">{formatMoney(payload[0].value as number)}</p>
      </div>
    );
  }
  return null;
};

export const Dashboard: React.FC<DashboardProps> = ({ records, isLoading, onMinistryClick }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState<'date' | 'value'>('date');
  const [filterCategory, setFilterCategory] = useState<string>('All');
  const [filterMethod, setFilterMethod] = useState<string>('All');

  // Refresh / Scrape State
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [refreshFeedback, setRefreshFeedback] = useState<{ type: 'success' | 'error' | 'info'; msg: string } | null>(null);

  // LOADING
  if (isLoading) {
    return (
        <div className="flex flex-col items-center justify-center min-h-[60vh] text-center animate-fadeIn">
            <RefreshCw className="w-10 h-10 text-gw-success animate-spin mb-4" />
            <h2 className="text-xl font-bold text-white">Loading Dashboard...</h2>
        </div>
    );
  }

  // --- REFRESH HANDLER ---
  const handleRefreshData = async () => {
    if (isRefreshing) return;
    setIsRefreshing(true);
    setRefreshFeedback({ type: 'info', msg: 'Scraping live data... This may take ~1 minute.' });

    try {
      const res = await fetch('/api/trigger-scrape', { method: 'POST' });
      const data = await res.json();

      if (data.success) {
        setRefreshFeedback({ type: 'success', msg: `Updated! Found ${data.count} records. Reloading...` });
        // Reload page to fetch the new data.json from disk
        setTimeout(() => window.location.reload(), 2000);
      } else {
         setRefreshFeedback({ type: 'error', msg: data.message || 'Scrape failed. Check logs.' });
         setIsRefreshing(false);
      }
    } catch (e) {
      setRefreshFeedback({ type: 'error', msg: 'Network error connecting to scraper.' });
      setIsRefreshing(false);
    }
  };

  // EMPTY STATE (Only if really 0 records)
  if (records.length === 0 && !isRefreshing) {
    return (
      <div className="flex flex-col items-center justify-center min-h-[60vh] text-center animate-fadeIn p-4">
        <div className="bg-gw-card border border-gw-border rounded-full p-6 mb-6">
          <FileText className="w-12 h-12 text-gw-muted" />
        </div>
        <h2 className="text-2xl font-bold text-white mb-2">No Records Available</h2>
        <p className="text-gw-muted max-w-md mb-8">
          The database is currently empty. Please update data via the Admin Panel or try refreshing below.
        </p>
        <button
            onClick={handleRefreshData}
            disabled={isRefreshing}
            className="flex items-center gap-2 px-6 py-3 bg-gw-success text-gw-bg rounded-lg font-bold hover:bg-gw-success/90 transition-all"
        >
            <RefreshCw className={`w-5 h-5 ${isRefreshing ? 'animate-spin' : ''}`} />
            {isRefreshing ? 'Running Scraper...' : 'Fetch Initial Data'}
        </button>
        {refreshFeedback && (
            <p className={`mt-4 text-sm ${refreshFeedback.type === 'error' ? 'text-gw-danger' : 'text-blue-400'}`}>
                {refreshFeedback.msg}
            </p>
        )}
      </div>
    );
  }

  // --- KPI LOGIC ---
  const totalSpend = records.reduce((acc, r) => acc + (r.amount || 0), 0);

  // Use Category for stats since "Method" is often vague in raw data
  const suppliesSpend = records
    .filter(r => (r.category || '').toLowerCase().includes('bekalan'))
    .reduce((acc, r) => acc + (r.amount || 0), 0);

  const worksSpend = records
    .filter(r => (r.category || '').toLowerCase().includes('kerja'))
    .reduce((acc, r) => acc + (r.amount || 0), 0);

  const servicesSpend = records
    .filter(r => (r.category || '').toLowerCase().includes('perkhidmatan'))
    .reduce((acc, r) => acc + (r.amount || 0), 0);

  // Last Updated Logic (Recent Crawl)
  const lastCrawlDate = records
    .map(r => r.crawledAt || '')
    .filter(d => d)
    .sort()
    .pop();

  // --- CHART DATA ---

  // 1. Pie Chart (Category Distribution)
  const categoryData = [
    { name: 'Works (Kerja)', value: worksSpend, color: '#ffadad' }, 
    { name: 'Supplies (Bekalan)', value: suppliesSpend, color: '#36d399' }, 
    { name: 'Services (Perkhidmatan)', value: servicesSpend, color: '#8da2ce' }, 
  ].filter(d => d.value > 0);

  // 2. Bar Chart (Top Ministries)
  const ministryTotals = records.reduce((acc: { [key: string]: number }, curr) => {
    if (!curr.ministry) return acc;
    // Translate the key for the chart
    const englishName = translateMinistry(curr.ministry);
    const currentVal = acc[englishName] || 0;
    acc[englishName] = currentVal + (curr.amount || 0);
    return acc;
  }, {});

  const barChartData = (Object.entries(ministryTotals) as [string, number][])
    .map(([name, value]) => ({ name, value }))
    .sort((a, b) => b.value - a.value)
    .slice(0, 8); // Top 8 only

  // --- TABLE FILTERING ---
  const filteredRecords = useMemo(() => {
    return records.filter(r => {
      const ministryRaw = (r.ministry || '').toLowerCase();
      const ministryEn = translateMinistry(r.ministry).toLowerCase();
      const vendor = (r.vendor || '').toLowerCase();
      const search = searchTerm.toLowerCase();

      // Search matches either raw Malay name, Translated English name, or Vendor
      const matchesSearch = ministryRaw.includes(search) || ministryEn.includes(search) || vendor.includes(search);

      // Category Filter
      const matchesCategory = filterCategory === 'All' || (r.category || '').includes(filterCategory);

      // Method Filter
      let matchesMethod = false;
      const methodRaw = (r.method || '').toLowerCase();
      if (filterMethod === 'All') {
          matchesMethod = true;
      } else if (filterMethod === 'Direct Negotiation') {
          // Robust check for English or Malay terms
          matchesMethod = methodRaw.includes('direct') || methodRaw.includes('rundingan');
      } else if (filterMethod === 'Open Tender') {
          matchesMethod = methodRaw.includes('open') || methodRaw.includes('tender') || methodRaw.includes('terbuka');
      } else {
          matchesMethod = methodRaw.includes(filterMethod.toLowerCase());
      }

      return matchesSearch && matchesCategory && matchesMethod;
    }).sort((a,b) => {
      // Fix: Ensure numeric arithmetic for sorting
      if (sortBy === 'value') return (b.amount || 0) - (a.amount || 0);

      // Default date sort (Handle invalid dates gracefully)
      const dateA = new Date(a.date).getTime() || 0;
      const dateB = new Date(b.date).getTime() || 0;
      return dateB - dateA;
    });
  }, [records, searchTerm, filterCategory, filterMethod, sortBy]);

  const handleExport = () => {
    downloadCSV(filteredRecords, `govwatch_export_${new Date().toISOString().split('T')[0]}.csv`);
  };

  return (
    <div className="space-y-8 animate-fadeIn pb-12">

      {/* Header with Refresh & Last Updated */}
      <div className="flex flex-col md:flex-row justify-end items-end md:items-center gap-4">

        {/* Feedback Message */}
        {refreshFeedback && (
            <div className={`text-xs px-3 py-1.5 rounded-full border font-medium ${
                refreshFeedback.type === 'error' ? 'bg-gw-danger/10 text-gw-danger border-gw-danger/20' :
                refreshFeedback.type === 'success' ? 'bg-gw-success/10 text-gw-success border-gw-success/20' :
                'bg-blue-500/10 text-blue-400 border-blue-500/20'
            }`}>
                {refreshFeedback.msg}
            </div>
        )}

        {/* Refresh Button */}
        <button 
            onClick={handleRefreshData}
            disabled={isRefreshing}
            className={`flex items-center gap-2 px-4 py-2 rounded-lg text-sm font-bold transition-all border shadow-sm ${
                isRefreshing 
                ? 'bg-gw-card text-gw-muted border-gw-border cursor-not-allowed opacity-70' 
                : 'bg-gw-success text-gw-bg hover:bg-gw-success/90 border-gw-success'
            }`}
        >
            <RefreshCw className={`w-4 h-4 ${isRefreshing ? 'animate-spin' : ''}`} />
            {isRefreshing ? 'Syncing...' : 'Refresh Data'}
        </button>

        {/* Last Updated Badge */}
        {lastCrawlDate && (
             <div className="flex items-center gap-2 text-xs text-gw-muted bg-gw-card px-3 py-2 rounded-lg border border-gw-border">
                <Clock className="w-3 h-3" />
                Updated: {new Date(lastCrawlDate).toLocaleDateString()}
             </div>
        )}
      </div>

      {/* KPI Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <StatCard label="Total Contract Value" value={formatMoney(totalSpend)} />
        <StatCard label="Works & Construction" value={formatMoney(worksSpend)} isAlert />
        <StatCard label="Supplies & Services" value={formatMoney(suppliesSpend + servicesSpend)} />
      </div>

      {/* Charts Row */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 min-h-[400px]">
         {/* Bar Chart (Horizontal) */}
         <div className="lg:col-span-2 bg-gw-card border border-gw-border rounded-lg p-6 flex flex-col">
            <h3 className="text-lg font-bold mb-4 text-white">Top Spending Ministries</h3>
            <div className="flex-1">
                <ResponsiveContainer width="100%" height="100%">
                    <BarChart data={barChartData} layout="vertical" margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
                        <XAxis type="number" hide />
                        <YAxis 
                            dataKey="name" 
                            type="category" 
                            width={180} 
                            tick={{ fill: '#e5e7ef', fontSize: 10, width: 170 }}
                            interval={0}
                        />
                        <Tooltip cursor={{fill: '#223055'}} content={<CustomTooltip />} />
                        <Bar dataKey="value" radius={[0, 4, 4, 0]}>
                            {barChartData.map((entry, index) => (
                                <Cell key={`cell-${index}`} fill={index === 0 ? '#ffadad' : '#36d399'} />
                            ))}
                        </Bar>
                    </BarChart>
                </ResponsiveContainer>
            </div>
         </div>

         {/* Pie Chart */}
         <div className="bg-gw-card border border-gw-border rounded-lg p-6 flex flex-col">
            <h3 className="text-lg font-bold mb-4 text-white">Category Breakdown</h3>
            <div className="flex-1">
                <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                        <Pie
                            data={categoryData}
                            cx="50%"
                            cy="50%"
                            innerRadius={60}
                            outerRadius={80}
                            paddingAngle={5}
                            dataKey="value"
                        >
                            {categoryData.map((entry, index) => (
                                <Cell key={`cell-${index}`} fill={entry.color} stroke="none" />
                            ))}
                        </Pie>
                        <Tooltip content={<CustomTooltip />} />
                        <Legend verticalAlign="bottom" height={36} iconType="circle" />
                    </PieChart>
                </ResponsiveContainer>
            </div>
         </div>
      </div>

      {/* Table Section */}
      <div className="bg-gw-card border border-gw-border rounded-lg overflow-hidden flex flex-col">
          {/* Table Controls */}
          <div className="p-4 border-b border-gw-border flex flex-col xl:flex-row justify-between items-center gap-4 bg-gw-bg/30">
            <div className="flex flex-col md:flex-row items-center gap-2 w-full xl:w-auto">
                 <h2 className="text-lg font-bold text-white flex items-center gap-2 mr-2">
                    <FileText className="w-5 h-5" /> Recent Awards
                 </h2>
                 <div className="flex flex-wrap items-center gap-2 justify-center md:justify-start w-full md:w-auto">
                    {/* Sort Dropdown */}
                    <div className="flex items-center bg-gw-bg border border-gw-border rounded px-2 py-1">
                        <ArrowUpDown className="w-4 h-4 text-gw-muted mr-2" />
                        <select 
                            value={sortBy} 
                            onChange={(e) => setSortBy(e.target.value as any)}
                            className="bg-transparent text-sm text-gw-text focus:outline-none"
                        >
                            <option value="date">Newest First</option>
                            <option value="value">Highest Value</option>
                        </select>
                    </div>

                    {/* Category Filter */}
                    <div className="flex items-center bg-gw-bg border border-gw-border rounded px-2 py-1">
                        <Filter className="w-4 h-4 text-gw-muted mr-2" />
                        <select 
                            value={filterCategory} 
                            onChange={(e) => setFilterCategory(e.target.value)}
                            className="bg-transparent text-sm text-gw-text focus:outline-none max-w-[120px]"
                        >
                            <option value="All">All Categories</option>
                            <option value="Kerja">Works</option>
                            <option value="Bekalan">Supplies</option>
                            <option value="Perkhidmatan">Services</option>
                        </select>
                    </div>

                    {/* Method Filter */}
                    <div className="flex items-center bg-gw-bg border border-gw-border rounded px-2 py-1">
                        <Briefcase className="w-4 h-4 text-gw-muted mr-2" />
                        <select 
                            value={filterMethod} 
                            onChange={(e) => setFilterMethod(e.target.value)}
                            className="bg-transparent text-sm text-gw-text focus:outline-none"
                        >
                            <option value="All">All Methods</option>
                            <option value="Open Tender">Open Tender</option>
                            <option value="Direct Negotiation">Direct Negotiation</option>
                        </select>
                    </div>
                 </div>
            </div>

            <div className="flex items-center gap-2 w-full xl:w-auto justify-end">
                <div className="relative w-full md:w-64">
                    <Search className="w-4 h-4 absolute left-3 top-1/2 -translate-y-1/2 text-gw-muted" />
                    <input 
                        type="text" 
                        placeholder="Search records..." 
                        className="w-full bg-gw-bg border border-gw-border text-sm rounded-full pl-10 pr-4 py-2 text-gw-text focus:outline-none focus:border-gw-success"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                </div>

                <button 
                  onClick={handleExport}
                  title="Download CSV"
                  className="p-2 bg-gw-bg border border-gw-border rounded-full text-gw-success hover:bg-gw-success/10 transition-colors shrink-0"
                >
                  <Download className="w-4 h-4" />
                </button>
            </div>
          </div>

          <div className="overflow-x-auto">
            <table className="w-full text-left text-sm">
              <thead className="bg-gw-bg/95">
                <tr>
                  <th className="px-6 py-4 font-bold text-gw-muted uppercase text-xs tracking-wider">Date</th>
                  <th className="px-6 py-4 font-bold text-gw-muted uppercase text-xs tracking-wider">Ministry</th>
                  <th className="px-6 py-4 font-bold text-gw-muted uppercase text-xs tracking-wider">Vendor</th>
                  <th className="px-6 py-4 font-bold text-gw-muted uppercase text-xs tracking-wider text-right">Value</th>
                  <th className="px-6 py-4 font-bold text-gw-muted uppercase text-xs tracking-wider text-right">Method & Category</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gw-border">
                {filteredRecords.slice(0, 50).map((r) => (
                  <tr key={r.id} className="hover:bg-gw-bg/50 transition-colors group">
                    <td className="px-6 py-4 whitespace-nowrap text-gw-muted font-mono text-xs">{r.date}</td>
                    <td className="px-6 py-4">
                      <button 
                        onClick={() => onMinistryClick(r.ministry)}
                        className="text-white hover:text-gw-success font-medium flex flex-col items-start transition-colors text-left"
                      >
                        <span className="line-clamp-1 max-w-[250px] font-bold" title={r.ministry}>
                            {translateMinistry(r.ministry)}
                        </span>
                        <span className="text-[10px] text-gw-muted flex items-center gap-1">
                            {r.ministry} 
                            <ArrowUpRight className="w-2 h-2 opacity-0 group-hover:opacity-100 transition-opacity" />
                        </span>
                      </button>
                    </td>
                    <td className="px-6 py-4 text-gw-muted truncate max-w-[200px]" title={r.vendor}>{r.vendor}</td>
                    <td className="px-6 py-4 text-right font-bold text-gw-text font-mono">{formatMoney(r.amount)}</td>
                    <td className="px-6 py-4 text-right whitespace-nowrap flex flex-col gap-1 items-end justify-center h-full">
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-[10px] font-medium border ${
                         (r.method || '').toLowerCase().includes('direct') || (r.method || '').toLowerCase().includes('rundingan')
                         ? 'bg-gw-danger/10 text-gw-danger border-gw-danger/20'
                         : 'bg-blue-400/10 text-blue-400 border-blue-400/20'
                      }`}>
                         {(r.method || 'Open Tender')}
                      </span>
                      <span className={`inline-flex items-center px-2 py-0.5 rounded text-[10px] text-gw-muted border border-gw-border`}>
                        {r.category || 'General'}
                      </span>
                    </td>
                  </tr>
                ))}
                {filteredRecords.length === 0 && (
                    <tr>
                        <td colSpan={5} className="text-center py-8 text-gw-muted italic">No records found matching your filters.</td>
                    </tr>
                )}
              </tbody>
            </table>
          </div>
          <div className="p-4 border-t border-gw-border bg-gw-bg/30 text-center text-xs text-gw-muted">
            Showing top {Math.min(filteredRecords.length, 50)} recent results
          </div>
      </div>
    </div>
  );
};

import puppeteer from 'puppeteer';
import fs from 'fs/promises';
import path from 'path';
import { fileURLToPath } from 'url';

const __dirname = path.dirname(fileURLToPath(import.meta.url));

// --- CONFIGURATION ---
const URL_DN = 'https://myprocurement.treasury.gov.my/archive/direct-negotiations';
const URL_TENDER = 'https://myprocurement.treasury.gov.my/archive/results-tender';
const OUTPUT_PATH = path.resolve(__dirname, '../public/data.json');
const LOG_DIR = path.resolve(__dirname, '../public/debug_logs');
const TIMEOUT_MS = 60000; 

// --- LOGGING SYSTEM ---
let logBuffer = "";

function log(message) {
    const timestamp = new Date().toISOString();
    const line = `[${timestamp}] ${message}`;
    console.log(line);
    logBuffer += line + "\n";
}

async function writeLogs() {
    try {
        await fs.mkdir(LOG_DIR, { recursive: true });
        await fs.writeFile(path.join(LOG_DIR, 'debug_log.txt'), logBuffer);
    } catch (e) {
        console.error("Failed to write logs to disk:", e);
    }
}

// --- UTILS ---
const sleep = (ms) => new Promise(r => setTimeout(r, ms));

async function autoScroll(page) {
    log("   -> Auto-scrolling to trigger lazy loading cards...");
    try {
        await page.evaluate(async () => {
            await new Promise((resolve) => {
                let totalHeight = 0;
                const distance = 200;
                const timer = setInterval(() => {
                    const scrollHeight = document.body.scrollHeight;
                    window.scrollBy(0, distance);
                    totalHeight += distance;
                    // Scroll deeper for cards
                    if(totalHeight >= scrollHeight || totalHeight > 10000){ 
                        clearInterval(timer);
                        resolve();
                    }
                }, 100);
            });
        });
    } catch (e) {
        log("   -> Scroll warning: " + e.message);
    }
}

async function scrape() {
  logBuffer = ""; 
  log(`=== STARTING SCRAPER V4 (Card Support) ===`);

  let browser;
  let allRecords = [];

  try {
    await fs.rm(LOG_DIR, { recursive: true, force: true }).catch(() => {});
    await fs.mkdir(LOG_DIR, { recursive: true });

    log("Launching Browser...");
    browser = await puppeteer.launch({
      headless: "new", 
      args: [
          '--no-sandbox', 
          '--disable-setuid-sandbox', 
          '--window-size=1920,1080', // Larger window for cards
          '--disable-web-security',
          '--disable-blink-features=AutomationControlled',
          '--disable-features=IsolateOrigins,site-per-process',
          // Note: Re-enabled images in case they are needed for layout, 
          // but if bandwidth is issue, might need to disable again.
          // Keeping disabled for speed.
          '--blink-settings=imagesEnabled=false' 
      ]
    });

    const page = await browser.newPage();

    await page.setRequestInterception(true);
    page.on('request', (req) => {
        const resourceType = req.resourceType();
        // Block fonts/images/media
        if (['image', 'font', 'media'].includes(resourceType)) {
            req.abort();
        } else {
            req.continue();
        }
    });

    await page.setUserAgent('Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36');
    await page.setViewport({ width: 1920, height: 1080 });

    // Console & API Sniffing
    page.on('console', msg => {
        if(!msg.text().includes('ERR_')) log(`[BROWSER] ${msg.text().substring(0, 100)}...`);
    });

    page.on('response', async (response) => {
        try {
            const url = response.url();
            const type = response.headers()['content-type'];
            if (type && (type.includes('json') || type.includes('plain')) && !url.includes('google')) {
                const text = await response.text();
                if (text.includes('amount') || text.includes('nilai')) {
                    // log(`[API] Captured potential JSON data from: ${url}`);
                    try {
                        const json = JSON.parse(text);
                        const extracted = normalizeApiData(json, url);
                        if (extracted.length > 0) allRecords.push(...extracted);
                    } catch(e) {}
                }
            }
        } catch (e) { }
    });

    // --- NAVIGATION ---
    const navigate = async (url, label) => {
        log(`Navigating to ${label} (Card Mode)...`);
        try {
            // Using load instead of networkidle to be faster
            await page.goto(url, { waitUntil: 'domcontentloaded', timeout: TIMEOUT_MS });

            // Wait for cards to likely appear
            await sleep(5000); 
            await autoScroll(page);
            await sleep(2000); // Wait after scroll

            await page.screenshot({ path: path.join(LOG_DIR, `${label}.png`) });
        } catch (e) {
            log(`[WARN] ${label} navigation issue: ${e.message}`);
            // Even if it timed out, we try to scrape what loaded
            try { await page.screenshot({ path: path.join(LOG_DIR, `${label}_timeout.png`) }); } catch {}
        }
    };

    await navigate(URL_DN, 'direct_nego');
    await navigate(URL_TENDER, 'tenders');

    // --- CARD-BASED VISUAL SCRAPE ---
    if (allRecords.length === 0) {
        log("No API data found. Attempting CARD-BASED DOM scrape...");
        const visualRecords = await scrapeVisual(page);
        log(`Visual scrape found ${visualRecords.length} items.`);
        allRecords.push(...visualRecords);
    }

    if (allRecords.length === 0) {
        log("!!! FAILURE: 0 RECORDS FOUND !!!");
        log("Tip: Use the Browser Script in the Upload tab.");
        const html = await page.content();
        await fs.writeFile(path.join(LOG_DIR, 'final_dom_dump.html'), html);
    } else {
        await saveData(allRecords);
    }

    log(`=== FINISHED. Records: ${allRecords.length} ===`);

  } catch (error) {
    log(`[CRITICAL] ${error.message}`);
  } finally {
    await writeLogs();
    if (browser) await browser.close();
  }
}

function normalizeApiData(json, sourceUrl) {
    const results = [];
    const now = new Date().toISOString();
    const findArrays = (obj) => {
        if (!obj) return;
        if (Array.isArray(obj)) {
            obj.forEach(item => {
                if (typeof item === 'object' && item !== null) {
                    const str = JSON.stringify(item).toLowerCase();
                    if (str.includes('nilai') || str.includes('harga') || str.includes('amount')) {
                        const ministry = item.kementerian || item.ministry || item.agency || "Unknown Ministry";
                        const vendor = item.nama_syarikat || item.vendor_name || item.petender || "Unknown Vendor";
                        const amountRaw = item.nilai_perolehan || item.harga_setuju_terima || item.amount || item.price || 0;
                        const date = item.tarikh_surat || item.date || new Date().toISOString().split('T')[0];

                        let amount = 0;
                        if (typeof amountRaw === 'number') amount = amountRaw;
                        else if (typeof amountRaw === 'string') amount = parseFloat(amountRaw.replace(/[^0-9.]/g, ''));

                        if (amount > 0) {
                            results.push({
                                ministry, vendor, amount,
                                method: sourceUrl.includes('direct') ? "Direct Negotiation" : "Open Tender",
                                category: "General",
                                date: String(date).split('T')[0], 
                                reason: item.reason || null, 
                                sourceUrl, crawledAt: now
                            });
                        }
                    }
                }
            });
        } else if (typeof obj === 'object') {
            Object.values(obj).forEach(val => findArrays(val));
        }
    };
    findArrays(json);
    return results;
}

// UPDATED: Looks for CARDS (Divs) instead of Tables
async function scrapeVisual(page) {
    return await page.evaluate(() => {
        const results = [];

        // Find all DIVs that might be cards
        const allDivs = Array.from(document.querySelectorAll('div, article, section'));

        // Heuristic: A card usually contains a price (RM) and a Ministry/Vendor label
        const potentialCards = allDivs.filter(div => {
            const txt = div.innerText || "";
            // Too short = likely just a label or button
            if (txt.length < 30) return false;
            // Too long = likely the whole body container
            if (txt.length > 2000) return false;

            // Must have currency
            if (!txt.includes("RM")) return false;

            // Must have some entity keyword
            const keywords = ["Kementerian", "Ministry", "Jabatan", "Syarikat", "Vendor", "Petender"];
            return keywords.some(kw => txt.includes(kw));
        });

        // Parse each potential card
        potentialCards.forEach(card => {
            const text = card.innerText;

            // 1. Extract Amount
            const moneyMatch = text.match(/RM\s?([0-9,.]+)/i);
            if (!moneyMatch) return; // Skip if no price found in this specific div
            const amount = parseFloat(moneyMatch[1].replace(/,/g, ''));
            if (isNaN(amount) || amount === 0) return;

            // 2. Extract Ministry
            let ministry = "Unknown Ministry";
            // Look for "Kementerian ..." up to newline
            const minMatch = text.match(/(?:Kementerian|Ministry|Jabatan|Agency)(.*?)(?:\n|$)/i);
            if (minMatch) ministry = minMatch[0].trim();

            // 3. Extract Vendor
            let vendor = "Unknown Vendor";
            const venMatch = text.match(/(?:Syarikat|Vendor|Petender|Oleh)(.*?)(?:\n|$)/i);
            if (venMatch) vendor = venMatch[0].replace(/(?:Syarikat|Vendor|Petender|Oleh)[:\s]*/i, '').trim();

            // 4. Extract Date
            let date = new Date().toISOString().split('T')[0];
            const dateMatch = text.match(/(\d{2}[-/.]\d{2}[-/.]\d{4})/);
            if (dateMatch) {
                // simple normalization if needed, mostly just take the string
                date = dateMatch[0];
            }

            // Deduplicate: If we already have a record with this exact amount and vendor (approx), skip
            // (Because nested divs might triggers this multiple times for the same card)
            const isDuplicate = results.some(r => r.amount === amount && r.vendor === vendor);

            if (!isDuplicate && ministry.length < 150 && vendor.length < 150) {
                 results.push({
                    ministry: ministry.replace(/[:]/g, '').trim(),
                    vendor: vendor.replace(/[:]/g, '').trim(),
                    amount,
                    method: document.location.href.includes('direct') ? "Direct Negotiation" : "Open Tender",
                    category: "General",
                    date,
                    sourceUrl: document.location.href,
                    crawledAt: new Date().toISOString()
                });
            }
        });

        return results;
    });
}

async function saveData(newRecords) {
    let finalData = [];
    try {
        const publicDir = path.dirname(OUTPUT_PATH);
        try { await fs.access(publicDir); } catch { await fs.mkdir(publicDir, { recursive: true }); }
        try { finalData = JSON.parse(await fs.readFile(OUTPUT_PATH, 'utf-8')); } catch { }

        const existingSigs = new Set(finalData.map(r => `${r.vendor}-${r.amount}-${r.ministry}`));
        newRecords.forEach(r => {
            const sig = `${r.vendor}-${r.amount}-${r.ministry}`;
            if (!existingSigs.has(sig)) {
                r.id = finalData.length + 1;
                finalData.push(r);
                existingSigs.add(sig);
            }
        });

        await fs.writeFile(OUTPUT_PATH, JSON.stringify(finalData, null, 2));
    } catch (e) { log(`Save Error: ${e.message}`); }
}

